import React, { useState, useEffect } from 'react';
import api from '../services/api';
import StoreForm from '../components/StoreForm';

function StoreListPage() {
    const [stores, setStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Modal form
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentStore, setCurrentStore] = useState(null);

    // Popup state
    const [openPopupId, setOpenPopupId] = useState(null);

    useEffect(() => {
        fetchStores();
    }, []);

    const fetchStores = async () => {
        try {
            const response = await api.get('/stores/chain/Pharmacity');
            setStores(response.data.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch stores.');
        } finally {
            setLoading(false);
        }
    };
    
    const handleSelectStore = (store) => {
    setCurrentStore(store);
    console.log("Store selected:", store);
    };

    const togglePopup = (storeId) => {
        setOpenPopupId(openPopupId === storeId ? null : storeId);
    };

    const handleViewDetail = (store) => {
        alert(`Thông tin chi tiết:\n- Chain: ${store.chain_name}\n- Store Code: ${store.store_code}\n- Address: ${store.address_vi}`);
        setOpenPopupId(null);
    };

    const handleLog = (store) => {
        console.log('Ghi log store:', store);
        alert(`Đã ghi log cho store ${store.store_code}`);
        setOpenPopupId(null);
    };

    if (loading) return <div>Loading stores...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div>
            <h2>Store Management</h2>
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th>Chain</th>
                        <th>Store Code</th>
                        <th>Address</th>
                        <th>Status</th>
                        <th>⋮</th>
                    </tr>
                </thead>
                <tbody>
                    {stores.map((store) => (
                        <tr key={store.store_id}>
                            <td>{store.chain_name}</td>
                            <td>{store.store_code}</td>
                            <td>{store.address_vi}</td>
                            <td
                                style={{
                                    color: store.is_active ? 'green' : 'red',
                                    fontWeight: 'bold',
                                }}
                            >
                                {store.is_active ? 'Active' : 'Inactive'}
                            </td>
                            <td style={{ position: 'relative' }}>
                                <button onClick={() => togglePopup(store.store_id)}>⋮</button>
                                {openPopupId === store.store_id && (
                                    <div
                                        style={{
                                            position: 'absolute',
                                            top: '100%',
                                            right: 0,
                                            background: '#fff',
                                            border: '1px solid #ccc',
                                            borderRadius: '4px',
                                            boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
                                            zIndex: 10,
                                        }}
                                    >
                                        <button
                                            style={{
                                                display: 'block',
                                                width: '100%',
                                                padding: '8px 12px',
                                                textAlign: 'left',
                                                border: 'none',
                                                background: 'transparent',
                                                cursor: 'pointer',
                                            }}
                                            onClick={() => handleViewDetail(store)}
                                        >
                                            Xem thông tin chi tiết
                                        </button>
                                        <button
                                            style={{
                                                display: 'block',
                                                width: '100%',
                                                padding: '8px 12px',
                                                textAlign: 'left',
                                                border: 'none',
                                                background: 'transparent',
                                                cursor: 'pointer',
                                            }}
                                            onClick={() => handleLog(store)}
                                        >
                                            Ghi log
                                        </button>
                                    </div>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Modal Form nếu vẫn muốn thêm/sửa */}
            <StoreForm
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSave={() => {}}
                initialData={currentStore}
            />
        </div>
    );
}

export default StoreListPage;
