// File: CampaignModal.js
import React, { useState, useEffect } from "react";
import Modal from "../Modal";

const defaultState = {
  name: "",
  description: "",
  start_date: "",
  end_date: "",
  is_active: true,
};

// Hàm tiện ích format ngày yyyy-mm-dd
const formatDate = (date) =>
  date ? new Date(date).toISOString().split("T")[0] : "";

function CampaignModal({ isOpen, onClose, onSave, initialData }) {
  const [campaignData, setCampaignData] = useState(defaultState);

  useEffect(() => {
    if (isOpen) {
      if (initialData) {
        setCampaignData({
          ...initialData,
          start_date: formatDate(initialData.start_date),
          end_date: formatDate(initialData.end_date),
        });
      } else {
        setCampaignData(defaultState);
      }
    }
  }, [initialData, isOpen]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setCampaignData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate ngày
    if (
      campaignData.start_date &&
      campaignData.end_date &&
      campaignData.end_date < campaignData.start_date
    ) {
      alert("End date cannot be before start date");
      return;
    }

    onSave(campaignData);
  };

  const isEdit = !!initialData;

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <form className="modal-content" onSubmit={handleSubmit}>
        <h3 className="modal-title">
          {isEdit ? "Edit Campaign" : "Add New Campaign"}
        </h3>

        <div className="modal-row">
          <div className="modal-label">Campaign Name:</div>
          <div className="modal-value">
            <input
              type="text"
              name="name"
              value={campaignData.name}
              onChange={handleChange}
              required
              className="form-control"
            />
          </div>
        </div>

        <div className="modal-row">
          <div className="modal-label">Description:</div>
          <div className="modal-value">
            <textarea
              name="description"
              value={campaignData.description}
              onChange={handleChange}
              rows={4}
              className="form-control"
            />
          </div>
        </div>

        <div className="modal-row">
          <div className="modal-label">Start Date:</div>
          <div className="modal-value">
            <input
              type="date"
              name="start_date"
              value={campaignData.start_date}
              onChange={handleChange}
              required
              className="form-control"
            />
          </div>
        </div>

        <div className="modal-row">
          <div className="modal-label">End Date:</div>
          <div className="modal-value">
            <input
              type="date"
              name="end_date"
              value={campaignData.end_date}
              onChange={handleChange}
              required
              className="form-control"
            />
          </div>
        </div>

        <div className="modal-row">
          <div className="modal-label">Active:</div>
          <div className="modal-value">
            <input
              type="checkbox"
              name="is_active"
              checked={campaignData.is_active}
              onChange={handleChange}
              style={{ marginRight: "8px" }}
            />
            Campaign is Active
          </div>
        </div>

        <div className="modal-actions">
          <button
            type="button"
            onClick={onClose}
            className="btn btn-danger btn-sm"
          >
            Cancel
          </button>
          <button type="submit" className="btn btn-primary btn-sm">
            {isEdit ? "Update" : "Save"}
          </button>
        </div>
      </form>
    </Modal>
  );
}

export default CampaignModal;
