import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import StoresMap from '../components/StoresMap';
import { districts, districtMap } from '../constants/locationData';

function DashBoardPage() {
    const [stores, setStores] = useState([]);
    const [campaignStores, setCampaignStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

// 1. fetch campaignStores mapping
const fetchCampaignStores = useCallback(async () => {
  try {
    const res = await api.get(`/campaigns/${campaignId}/stores`);
    setCampaignStores(res.data.data); // array [{ campaign_id, store_id }]
  } catch (err) {
    setError('Failed to load campaign store data.');
  }
}, [campaignId]);

// 2. fetch all stores
const fetchStores = useCallback(async () => {
  try {
    const res = await api.get('/stores');
    let allStores = res.data.data;

    if (campaignStores.length > 0) {
      const storeIds = campaignStores.map(cs => cs.store_id);
      allStores = allStores.filter(store => storeIds.includes(store.id));
    }
    setStores(allStores);
  } catch (err) {
    setError('Failed to load store data.');
  } finally {
    setLoading(false);
  }
}, [campaignStores]);


    // Fetch campaignStores first, then stores
    useEffect(() => {
        fetchCampaignStores();
    }, [fetchCampaignStores]);

    useEffect(() => {
        if (campaignStores.length > 0) {
            fetchStores();
        }
    }, [campaignStores, fetchStores]);

    return (
        <div className="container">
            <div className="page-header">
                <h2>Dashboard Overview</h2>
            </div>
            <div className="card">
                <div className="card-header">
                    <h4>Store Locations</h4>
                </div>
                <div className="card-body">
                    {loading && <p>Loading map data...</p>}
                    {error && <p className="error-message">{error}</p>}
                    {!loading && !error && (
                        <StoresMap 
                            stores={stores} 
                            districts={districts} 
                            districtMap={districtMap} 
                            campaignStores={campaignStores}
                        />
                    )}
                </div>
            </div>
        </div>
    );
}

export default DashBoardPage;
