import React, { useState, useEffect } from 'react';
import api from '../services/api';

function StoreListPage() {
    const [stores, setStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // --- NEW: State for the create form ---
    const [newStore, setNewStore] = useState({ name: '', address: '' });

    // --- Fetch initial data ---
    useEffect(() => {
        const fetchStores = async () => {
            try {
                const response = await api.get('/stores');
                setStores(response.data.data);
            } catch (err) {
                setError('Failed to fetch stores.');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchStores();
    }, []);

    // --- NEW: Handler for form input changes ---
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewStore(prevState => ({ ...prevState, [name]: value }));
    };

    // --- NEW: Handler for creating a store ---
    const handleCreateStore = async (e) => {
        e.preventDefault();
        if (!newStore.name || !newStore.address || !newStore.latitude || !newStore.longitude) {
            alert('Please fill in all fields');
            return;
        }
        try {
            const response = await api.post('/stores', newStore);
            // Add the new store to the top of the list
            setStores([response.data.data, ...stores]);
            // Clear the form
            setNewStore({ name: '', address: '', latitude: '', longitude: '' });
        } catch (err) {
            setError('Failed to create store.');
            console.error(err);
        }
    };

    // --- NEW: Handler for deleting a store ---
    const handleDeleteStore = async (storeId) => {
        if (window.confirm('Are you sure you want to delete this store?')) {
            try {
                await api.delete(`/stores/${storeId}`);
                // Remove the deleted store from the list
                setStores(stores.filter(store => store.id !== storeId));
            } catch (err) {
                setError('Failed to delete store.');
                console.error(err);
            }
        }
    };


    if (loading) return <div>Loading stores...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div>
            <h2>Store Management</h2>

            {/* --- NEW: Create Store Form --- */}
            <form onSubmit={handleCreateStore} style={{ marginBottom: '20px' }}>
                <h3>Add New Store</h3>
                <input
                    type="text"
                    name="name"
                    placeholder="Store Name"
                    value={newStore.name}
                    onChange={handleInputChange}
                    required
                    style={{ marginRight: '10px' }}
                />
                <input
                    type="text"
                    name="address"
                    placeholder="Address"
                    value={newStore.address}
                    onChange={handleInputChange}
                    required
                    style={{ marginRight: '10px' }}
                />
                <input
                    type="text"
                    name="latitude"
                    placeholder="latitude"
                    value={newStore.latitude}
                    onChange={handleInputChange}
                    required
                    style={{ marginRight: '10px' }}
                />
                <input
                    type="text"
                    name="longitude"
                    placeholder="Longitude"
                    value={newStore.longitude}
                    onChange={handleInputChange}
                    required
                    style={{ marginRight: '10px' }}
                />
                <button type="submit">Create Store</button>
            </form>

            {/* --- Table of Stores (with new Actions column) --- */}
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Address</th>
                        <th>Status</th>
                        <th>Actions</th> {/* NEW Column */}
                    </tr>
                </thead>
                <tbody>
                    {stores.length > 0 ? (
                        stores.map(store => (
                            <tr key={store.id}>
                                <td>{store.name}</td>
                                <td>{store.address}</td>
                                <td>{store.status}</td>
                                <td>
                                    {/* --- NEW: Delete Button --- */}
                                    <button onClick={() => handleDeleteStore(store.id)}>
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4">No stores found.</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
}

export default StoreListPage;
