// File: StoresMap.jsx
import React, { useState, useMemo, memo, useRef } from 'react';
import { GoogleMap, useJsApiLoader, InfoWindow, Marker } from '@react-google-maps/api';

const MAP_CONTAINER_STYLE = {
  width: '100%',
  height: '600px',
  borderRadius: '8px',
};

const DEFAULT_CENTER = { lat: 10.7769, lng: 106.7009 };

function StoresMap({ stores, districts, districtMap, campaigns, campaignStores }) {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  const [selectedStore, setSelectedStore] = useState(null);
  const [selectedBoard, setSelectedBoard] = useState("all");
  const [selectedDistrict, setSelectedDistrict] = useState("all");
  const [selectedDistrictRaw, setSelectedDistrictRaw] = useState("all");
  const [selectedCampaign, setSelectedCampaign] = useState("all");

  const mapRef = useRef(null);

  const onMapLoad = (map) => {
    mapRef.current = map;
  };

  const handleReset = () => {
    setSelectedBoard("all");
    setSelectedDistrict("all");
    setSelectedDistrictRaw("all");
    setSelectedCampaign("all");
    setSelectedStore(null);

    if (mapRef.current) {
      mapRef.current.setCenter(DEFAULT_CENTER);
      mapRef.current.setZoom(12);
    }
  };

  const boardOptions = useMemo(() => {
    if (!stores || stores.length === 0) return [];
    return [...new Set(stores.map(s => s.board_name))].sort();
  }, [stores]);

  if (!isLoaded) return <div>Loading Map...</div>;

  const districtRawOptions = selectedDistrict !== "all"
    ? districtMap[selectedDistrict] || []
    : [];

  const filteredStores = stores.filter(store => {
    const boardMatch = selectedBoard === "all" || store.board_name === selectedBoard;
    const districtMatch = selectedDistrict === "all" || districtMap[selectedDistrict]?.includes(store.district_raw);
    const districtRawMatch = selectedDistrictRaw === "all" || store.district_raw === selectedDistrictRaw;
    const campaignMatch =
      selectedCampaign === "all" ||
      campaignStores.some(cs =>
        String(cs.campaign_id) === String(selectedCampaign) &&
        String(cs.store_id) === String(store.id)
      );

    return boardMatch && districtMatch && districtRawMatch && campaignMatch;
  });

  return (
    <div>
      {/* Bộ lọc */}
      <div className="map-filters-container">
        {/* Filter Board */}
        <div className="form-group">
          <label htmlFor="board-filter">Lọc theo chuỗi:</label>
          <select
            id="board-filter"
            value={selectedBoard}
            onChange={(e) => setSelectedBoard(e.target.value)}
            className="form-control"
          >
            <option value="all">Tất cả</option>
            {boardOptions.map(board => (
              <option key={board} value={board}>{board}</option>
            ))}
          </select>
        </div>

        {/* Filter Campaign */}
        <div className="form-group">
          <label htmlFor="campaign-filter">Lọc theo Chiến dịch:</label>
          <select
            id="campaign-filter"
            value={selectedCampaign}
            onChange={(e) => setSelectedCampaign(e.target.value)}
            className="form-control"
          >
            <option value="all">Tất cả</option>
            {campaigns.map(c => (
              <option key={c.id} value={c.id}>{c.name}</option>
            ))}
          </select>
        </div>

        {/* Filter Province/City */}
        <div className="form-group">
          <label htmlFor="province-filter">Lọc theo tỉnh/thành:</label>
          <select
            id="province-filter"
            value={selectedDistrict}
            onChange={(e) => {
              setSelectedDistrict(e.target.value);
              setSelectedDistrictRaw("all");
            }}
            className="form-control"
          >
            <option value="all">Tất cả</option>
            {districts.map(d => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </div>

        {/* Filter District Raw */}
        {selectedDistrict !== "all" && (
          <div className="form-group">
            <label htmlFor="district-filter">Lọc theo Quận/Huyện:</label>
            <select
              id="district-filter"
              value={selectedDistrictRaw}
              onChange={(e) => setSelectedDistrictRaw(e.target.value)}
              className="form-control"
            >
              <option value="all">Tất cả</option>
              {districtRawOptions.map(raw => (
                <option key={raw} value={raw}>{raw}</option>
              ))}
            </select>
          </div>
        )}

        {/* 🔹 Button Reset */}
          <button onClick={handleReset} className="btn btn-outline">
               Reset Map
          </button>
      </div>

      {/* Bản đồ */}
      <GoogleMap
        mapContainerStyle={MAP_CONTAINER_STYLE}
        center={DEFAULT_CENTER}
        zoom={12}
        onLoad={onMapLoad}
      >
        {filteredStores.map(store => (
          <Marker
            key={store.id}
            position={{
              lat: Number(store.latitude),
              lng: Number(store.longitude)
            }}
            onClick={() => setSelectedStore(store)}
          />
        ))}

        {selectedStore && (
          <InfoWindow
            position={{
              lat: Number(selectedStore.latitude),
              lng: Number(selectedStore.longitude)
            }}
            onCloseClick={() => setSelectedStore(null)}
          >
            <div className="popupstoredetail">
              <h3>{selectedStore.store_code || selectedStore.name}</h3>
              <p>{selectedStore.address}</p>
              <p><b>Board:</b> {selectedStore.board_name}</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </div>
  );
}

export default memo(StoresMap);
