// src/pages/CampaignDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';

function CampaignDetailPage() {
  const [campaign, setCampaign] = useState(null);
  const [stores, setStores] = useState([]); // stores Ä‘Ã£ gÃ¡n cho campaign
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { id } = useParams();

  useEffect(() => {
    const fetchCampaign = async () => {
      try {
        const [campaignRes, storesRes] = await Promise.all([
          api.get(`/campaigns/${id}`),
          api.get(`/campaigns/${id}/stores`), // API danh sÃ¡ch store theo campaign
        ]);
        setCampaign(campaignRes.data.data);
        setStores(storesRes.data.data || []);
      } catch (err) {
        setError('Failed to fetch campaign details.');
      } finally {
        setLoading(false);
      }
    };
    fetchCampaign();
  }, [id]);

  if (loading) return <div>Loading campaign...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;
  if (!campaign) return <div>Campaign not found.</div>;

  return (
    <div>
      <Link to="/campaigns" className="mb-20" style={{ display: 'block' }}>
        &larr; Back to Campaign List
      </Link>

      <div className="profile-page">
        {/* --- Cá»™t trÃ¡i: Campaign Info --- */}
        <div className="profile-sidebar">
          <div className="card">
            <div className="profile-avatar">
              <span>ðŸ“¢</span>
            </div>
            <h3>{campaign.name}</h3>
            <p className="text-muted">{campaign.status}</p>
          </div>
        </div>

        {/* --- Cá»™t pháº£i: Campaign Details + Stores --- */}
        <div className="profile-main">
          <div className="card">
            <div className="profile-details">
              <div className="detail-row">
                <div className="detail-label">Name</div>
                <div className="detail-value">{campaign.name}</div>
              </div>
              <div className="detail-row">
                <div className="detail-label">Start Date</div>
                <div className="detail-value">
                  {new Date(campaign.start_date).toLocaleDateString('vi-VN')}
                </div>
              </div>
              <div className="detail-row">
                <div className="detail-label">End Date</div>
                <div className="detail-value">
                  {new Date(campaign.end_date).toLocaleDateString('vi-VN')}
                </div>
              </div>
            </div>
          </div>

          {/* --- Báº£ng stores --- */}
          <div className="card mt-20">
            <div className="card-header">
              <h4>Stores in this Campaign</h4>
              <button className="btn btn-primary btn-sm">
                + Assign Store
              </button>
            </div>
            <table className="table">
              <thead>
                <tr>
                  <th>Store Name</th>
                  <th>Address</th>
                  <th>City</th>
                  <th>Status</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {stores.length > 0 ? (
                  stores.map((store) => (
                    <tr key={store.id}>
                      <td>{store.name}</td>
                      <td>{store.address}</td>
                      <td>{store.city}</td>
                      <td>
                        <span
                          className={`status status-${
                            store.is_active ? 'active' : 'inactive'
                          }`}
                        >
                          {store.is_active ? 'Active' : 'Inactive'}
                        </span>
                      </td>
                      <td>
                        <button className="btn btn-danger btn-sm">
                          Remove
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" style={{ textAlign: 'center' }}>
                      No stores assigned yet.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CampaignDetailPage;
