import React, { useState, useEffect } from 'react';
import Modal from './Modal';

function StoreForm({ isOpen, onClose, onSave, initialData }) {
    const [storeData, setStoreData] = useState({ name: '', address: '', latitude: '', longitude: '', status: 'active' });

    // When initialData changes (i.e., when opening the modal for editing),
    // populate the form with that data.
    useEffect(() => {
        if (initialData) {
            setStoreData(initialData);
        } else {
            // Reset for 'create' mode
            setStoreData({ name: '', address: '', latitude: '', longitude: '', status: 'active' });
        }
    }, [initialData, isOpen]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setStoreData(prevState => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(storeData);
    };

    return (
        <Modal isOpen={isOpen} onClose={onClose}>
            <form onSubmit={handleSubmit}>
                <h2>{initialData ? 'Edit Store' : 'Add New Store'}</h2>
                <div style={{ marginBottom: '10px' }}>
                    <label>Name:</label><br/>
                    <input type="text" name="name" value={storeData.name} onChange={handleChange} required style={{ width: '95%' }}/>
                </div>
                <div style={{ marginBottom: '10px' }}>
                    <label>Address:</label><br/>
                    <input type="text" name="address" value={storeData.address} onChange={handleChange} required style={{ width: '95%' }}/>
                </div>
                <div style={{ marginBottom: '10px' }}>
                    <label>Latitude:</label><br/>
                    <input type="text" name="latitude" value={storeData.latitude} onChange={handleChange} required style={{ width: '95%' }}/>
                </div>
                <div style={{ marginBottom: '10px' }}>
                    <label>Longitude:</label><br/>
                    <input type="text" name="longitude" value={storeData.longitude} onChange={handleChange} required style={{ width: '95%' }}/>
                </div>
                 <div style={{ marginBottom: '10px' }}>
                    <label>Status:</label><br/>
                    <input type="text" name="status" value={storeData.status} onChange={handleChange} required style={{ width: '95%' }}/>
                </div>
                <button type="submit" style={{ marginRight: '10px' }}>Save</button>
                <button type="button" onClick={onClose}>Cancel</button>
            </form>
        </Modal>
    );
}

export default StoreForm;
