import React from 'react';
import Modal from './Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt } from '@fortawesome/free-solid-svg-icons';

function StoreDetailModal({ isOpen, onClose, store }) {
  if (!isOpen || !store) {
    return null;
  }

  // 1. SỬA LẠI URL GOOGLE MAPS CHO ĐÚNG CHUẨN
  // Link để mở trong tab mới
  const googleMapsUrl = `https://www.google.com/maps?q=${store.latitude},${store.longitude}`;
  
  // Link để nhúng vào iframe (yêu cầu API Key)
  const googleMapsEmbedUrl = `https://www.google.com/maps/embed/v1/view?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&center=${store.latitude},${store.longitude}&zoom=16`;

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      {/* 2. Áp dụng cấu trúc và class CSS nhất quán */}
      <div className="card-body">
        <h3 style={{ marginBottom: '20px' }}>Store Details</h3>

        <div className="profile-details">
          <div className="detail-row">
            <div className="detail-label">Board Name:</div>
            <div className="detail-value">{store.board_name}</div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Store Code:</div>
            <div className="detail-value">{store.store_code}</div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Address:</div>
            <div className="detail-value">{store.address}</div> {/* Cập nhật address_vi */}
          </div>
          <div className="detail-row">
            <div className="detail-label">District:</div>
            <div className="detail-value">{store.district}</div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Status:</div>
            <div className="detail-value">
              <span className={`status status-${store.is_active ? 'active' : 'inactive'}`}>
                {store.is_active ? 'Active' : 'Inactive'}
              </span>
            </div>
          </div>
          <div className="detail-row">
             <div className="detail-label">Location:</div>
             <div className="detail-value">
                {process.env.REACT_APP_GOOGLE_MAPS_API_KEY ? (
                  <iframe
                    className="map-iframe"
                    src={googleMapsEmbedUrl}
                    allowFullScreen
                    loading="lazy"
                    referrerPolicy="no-referrer-when-downgrade"
                    title="Google Maps Location"
                  />
                ) : (
                  <p>Google Maps API Key not configured.</p>
                )}
                <a href={googleMapsUrl} target="_blank" rel="noopener noreferrer">
                  <button className="btn btn-outline" style={{gap: '8px'}}>
                    <FontAwesomeIcon icon={faMapMarkerAlt} />
                    View on Google Maps
                  </button>
                </a>
             </div>
          </div>
        </div>

        <div className="modal-actions">
           <button className="btn btn-danger" onClick={onClose}>Close</button>
        </div>
      </div>
    </Modal>
  );
}

export default StoreDetailModal;
