import React, { useState } from 'react';
import { GoogleMap, useJsApiLoader, InfoWindow, Marker } from '@react-google-maps/api';

const mapContainerStyle = {
  width: '100%',
  height: '600px',
  borderRadius: '8px',
};

const defaultCenter = {
  lat: 10.7769,
  lng: 106.7009
};

// Hàm chọn màu theo board_name
const getMarkerIcon = (boardName) => {
  const colorMap = {
    A: "http://maps.google.com/mapfiles/ms/icons/blue-dot.png",
    B: "http://maps.google.com/mapfiles/ms/icons/yellow-dot.png",
    C: "http://maps.google.com/mapfiles/ms/icons/purple-dot.png",
  };

  return colorMap[boardName] || "http://maps.google.com/mapfiles/ms/icons/red-dot.png"; 
};

function StoresMap({ stores }) {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  });

  const [selectedStore, setSelectedStore] = useState(null);

  if (!isLoaded) return <div>Loading Map...</div>;

  return (
    <GoogleMap
      mapContainerStyle={mapContainerStyle}
      center={defaultCenter}
      zoom={12}
    >
      {stores.map((store) => (
        <Marker
          key={store.id}
          position={{
            lat: parseFloat(store.latitude),
            lng: parseFloat(store.longitude)
          }}
          icon={getMarkerIcon(store.board_name)}  // 👈 đổi màu theo board_name
          onClick={() => setSelectedStore(store)}
        />
      ))}

      {selectedStore && (
        <InfoWindow
          position={{
            lat: parseFloat(selectedStore.latitude),
            lng: parseFloat(selectedStore.longitude)
          }}
          onCloseClick={() => setSelectedStore(null)}
        >
          <div>
            <h4>{selectedStore.display_name || selectedStore.name}</h4>
            <p>{selectedStore.address}</p>
            <p><b>Board:</b> {selectedStore.board_name}</p>
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
}

export default React.memo(StoresMap);
