import React, { useState, useEffect } from 'react';
import api from '../services/api';
import UserForm from '../components/UserForm';
import UserDetailModal from '../components/UserDetailModal'; // <-- BƯỚC 1: IMPORT MODAL DETAIL


function UserListPage() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentUser, setCurrentUser] = useState(null);

    const [isViewModalOpen, setIsViewModalOpen] = useState(false);
    const [selectedUser, setSelectedUser] = useState(null);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await api.get('/users');
                setUsers(response.data.data);
            } catch (err) {
                setError('Failed to fetch users.');
            } finally {
                setLoading(false);
            }
        };
        fetchUsers();
    }, []);

    const handleOpenCreateModal = () => {
        setCurrentUser(null);
        setIsModalOpen(true);
    };

    const handleOpenEditModal = (user) => {
        setCurrentUser(user);
        setIsModalOpen(true);
    };

    const handleViewDetail = (user) => {
        setSelectedUser(user);
        setIsViewModalOpen(true);
    };
    
    const handleSaveUser = async (userData) => {
        try {
            if (userData.id) {
                const { id, ...updateData } = userData;
                const response = await api.put(`/users/${id}`, updateData);
                setUsers(users.map(u => u.id === id ? response.data.data : u));
            } else {
                const response = await api.post('/users', userData);
                setUsers([response.data.data, ...users]);
            }
            setIsModalOpen(false);
        } catch (err) {
            setError(userData.id ? 'Failed to update user.' : 'Failed to create user.');
        }
    };
    
    const handleDeactivateUser = async (user) => {
        if (window.confirm(`Are you sure you want to deactivate user ${user.full_name}?`)) {
            try {
                const updatedUser = { ...user, is_active: false };
                const response = await api.put(`/users/${user.id}`, updatedUser);
                setUsers(users.map(u => u.id === user.id ? response.data.data : u));
            } catch (err) {
                setError('Failed to deactivate user.');
            }
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div style={{color: 'red'}}>{error}</div>;

    return (
        <div>
            <h2>User Management</h2>
            <button onClick={handleOpenCreateModal} className="mb-20" style={{ float: 'right' }}>
                + Add New User
            </button>
            
            <div className="table-wrapper">
                <table className="styled-table">
                    <thead>
                        <tr>
                            <th>Full Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td>{user.full_name}</td>
                                <td>{user.email}</td>
                                <td>{user.role}</td>
                                <td style={{ color: user.is_active ? 'green' : 'red', fontWeight: 'bold' }}>
                                    {user.is_active ? 'Active' : 'Inactive'}
                                </td>
                                <td className="actions-cell">
                                    <button onClick={() => handleViewDetail(user)}>View</button>
                                    <button className='edit' onClick={() => handleOpenEditModal(user)}>Edit</button>
                                    {user.is_active && (
                                        <button
                                            onClick={() => handleDeactivateUser(user)}
                                            style={{ backgroundColor: '#f44336' }}
                                        >
                                            Deactivate
                                        </button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            
            <UserForm
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSave={handleSaveUser}
                initialData={currentUser}
            />
            <UserDetailModal 
                isOpen={isViewModalOpen}
                onClose={() => setIsViewModalOpen(false)}
                user={selectedUser}
            />
        </div>
    );
}

export default UserListPage;
