import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { Link } from 'react-router-dom';
import CampaignForm from '../components/CampaignForm';

function CampaignListPage() {
  const [campaigns, setCampaigns] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentCampaign, setCurrentCampaign] = useState(null);

  useEffect(() => {
    const fetchCampaigns = async () => {
      try {
        const response = await api.get('/campaigns');
        setCampaigns(response.data.data);
      } catch (err) {
        setError('Failed to fetch campaigns.');
      } finally {
        setLoading(false);
      }
    };
    fetchCampaigns();
  }, []);

  const handleOpenCreateModal = () => {
    setCurrentCampaign(null);
    setIsModalOpen(true);
  };

  const handleOpenEditModal = (campaign) => {
    setCurrentCampaign(campaign);
    setIsModalOpen(true);
  };

  const handleSaveCampaign = async (campaignData) => {
    try {
      if (campaignData.id) {
        const { id, ...updateData } = campaignData;
        const response = await api.put(`/campaigns/${id}`, updateData);
        setCampaigns(campaigns.map(c => c.id === id ? response.data.data : c));
      } else {
        const response = await api.post('/campaigns', campaignData);
        setCampaigns([response.data.data, ...campaigns]);
      }
      setIsModalOpen(false);
    } catch (err) {
      setError(campaignData.id ? 'Failed to update campaign.' : 'Failed to create campaign.');
    }
  };

  const handleDeleteCampaign = async (campaign) => {
    if (window.confirm(`Are you sure you want to delete campaign "${campaign.name}"?`)) {
      try {
        await api.delete(`/campaigns/${campaign.id}`);
        setCampaigns(campaigns.filter(c => c.id !== campaign.id));
      } catch (err) {
        setError('Failed to delete campaign.');
      }
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
        <div className="page-header">
            <h2>Campaign Management</h2>
            <button onClick={handleOpenCreateModal} className="btn btn-primary">
                + Add New Campaign
            </button>
        </div>
      <div className="table-wrapper">
        <table className="styled-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Start</th>
              <th>End</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {campaigns.map(campaign => (
              <tr key={campaign.id}>
                <td>
                    <Link to={`/campaigns/${campaign.id}`}>
                        {campaign.name}
                    </Link>
                </td>
                <td>{campaign.description}</td>
                <td>{new Date(campaign.start_date).toLocaleDateString()}</td>
                <td>{new Date(campaign.end_date).toLocaleDateString()}</td>
                <td style={{ color: campaign.status === 'active' ? 'green' : 'gray', fontWeight: 'bold' }}>
                  {campaign.status}
                </td>
                <td className=".form-btn">
                  <button className='btn btn-outline' onClick={() => handleOpenEditModal(campaign)}>Edit</button>
                  <button
                    onClick={() => handleDeleteCampaign(campaign)}
                    className='btn btn-danger'
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <CampaignForm
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={handleSaveCampaign}
        initialData={currentCampaign}
      />
    </div>
  );
}

export default CampaignListPage;
