import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import UserForm from '../components/UserForm';

function UserListPage() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  // Fetch users
  const fetchUsers = useCallback(async () => {
    setLoading(true);
    try {
      const response = await api.get('/users');
      setUsers(response.data.data);
    } catch {
      setError('Failed to fetch users.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  const openCreateModal = () => {
    setCurrentUser(null);
    setIsModalOpen(true);
  };

  const openEditModal = (user) => {
    setCurrentUser(user);
    setIsModalOpen(true);
  };

  const saveUser = async (userData) => {
    try {
      if (userData.id) {
        const { id, ...updateData } = userData;
        const response = await api.put(`/users/${id}`, updateData);
        setUsers(users.map(u => u.id === id ? response.data.data : u));
      } else {
        const response = await api.post('/users', userData);
        setUsers([response.data.data, ...users]);
      }
      setIsModalOpen(false);
    } catch {
      setError(userData.id ? 'Failed to update user.' : 'Failed to create user.');
    }
  };

  const deactivateUser = async (user) => {
    if (!window.confirm(`Are you sure you want to deactivate user ${user.full_name}?`)) return;
    try {
      const updatedUser = { ...user, is_active: false };
      const response = await api.put(`/users/${user.id}`, updatedUser);
      setUsers(users.map(u => u.id === user.id ? response.data.data : u));
    } catch {
      setError('Failed to deactivate user.');
    }
  };

  if (loading) return <div className="loading">Loading users...</div>;
  if (error) return <div className="error-message">{error}</div>;

  // Component con cho mỗi row
  const UserRow = ({ user }) => (
    <tr>
      <td>
        <Link to={`/users/${user.id}`} className="table-link">
          {user.full_name}
        </Link>
      </td>
      <td>{user.email}</td>
      <td>{user.role}</td>
      <td>
        <span className={`status ${user.is_active ? 'status-active' : 'status-inactive'}`}>
          {user.is_active ? 'Active' : 'Inactive'}
        </span>
      </td>
      <td className="text-center flex gap-1 justify-center">
        <button
          className="btn btn-outline btn-sm"
          onClick={() => openEditModal(user)}
        >
          Edit
        </button>
        {user.is_active && (
          <button
            className="btn btn-danger btn-sm"
            onClick={() => deactivateUser(user)}
          >
            Deactivate
          </button>
        )}
      </td>
    </tr>
  );

  return (
    <div className="container">
      {/* Header */}
      <div className="page-header">
        <h2 className="page-title">User Management</h2>
        <button className="btn btn-primary" onClick={openCreateModal}>
          + Add New User
        </button>
      </div>

      {/* Table */}
      <div className="table-wrapper overflow-x-auto">
        <table className="table w-full">
          <thead>
            <tr>
              <th>Full Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Status</th>
              <th className="text-center">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.length === 0 ? (
              <tr>
                <td colSpan="5" className="table-empty text-center">
                  No users found.
                </td>
              </tr>
            ) : (
              users.map(user => <UserRow key={user.id} user={user} />)
            )}
          </tbody>
        </table>
      </div>

      {/* Modal */}
      <UserForm
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={saveUser}
        initialData={currentUser}
      />
    </div>
  );
}

export default UserListPage;
