import React, { useState, useEffect } from 'react';
import Modal from './Modal';

function UserForm({ isOpen, onClose, onSave, initialData }) {
    const [userData, setUserData] = useState({});
    
    useEffect(() => {
        if (initialData) {
            setUserData(initialData);
        } else {
            // Reset state cho việc tạo mới
            setUserData({ full_name: '', email: '', password: '', phone_number: '', role: 'parttime', is_active: true });
        }
    }, [initialData, isOpen]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setUserData(prevState => ({
            ...prevState,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(userData);
    };

    return (
        <Modal isOpen={isOpen} onClose={onClose}>
            <form onSubmit={handleSubmit}>
                <h2>{initialData ? 'Edit User' : 'Add New User'}</h2>
                {/* ... (trường Full Name) ... */}
                {/* Chỉ hiện các trường này khi tạo mới */}
                {!initialData && (
                    <>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Email:</label><br/>
                            <input type="email" name="email" value={userData.email || ''} onChange={handleChange} required style={{ width: '95%' }}/>
                        </div>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Password:</label><br/>
                            <input type="password" name="password" value={userData.password || ''} onChange={handleChange} required style={{ width: '95%' }}/>
                        </div>
                         <div style={{ marginBottom: '10px' }}>
                            <label>Phone Number:</label><br/>
                            <input type="tel" name="phone_number" value={userData.phone_number || ''} onChange={handleChange} style={{ width: '95%' }}/>
                        </div>
                    </>
                )}
                {/* ... (trường Role và Active) ... */}
                <button type="submit">Save</button>
            </form>
        </Modal>
    );
}

export default UserForm;
