import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';
import AssignStoreModal from '../components/AssignStoreModal';

function CampaignDetailPage() {
  const [campaign, setCampaign] = useState(null);
  const [assignedStores, setAssignedStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { id } = useParams();
  const [isModalOpen, setIsModalOpen] = useState(false);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const [campaignRes, storesRes] = await Promise.all([
        api.get(`/campaigns/${id}`),
        api.get(`/campaigns/${id}/stores`)
      ]);
      setCampaign(campaignRes.data.data);
      setAssignedStores(storesRes.data.data || []);
    } catch (err) {
      setError('Failed to fetch campaign details.');
    } finally {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchData();
  }, [fetchData]); 

  const handleSaveStores = () => {
    setIsModalOpen(false);
    fetchData();
  };

  if (loading) return <div>Loading campaign...</div>;
  if (error) return <div className="container error-message">{error}</div>;
  if (!campaign) return <div>Campaign not found.</div>;

  return (
    // 1. Bọc toàn bộ trong .container
    <div className="container">
      <Link to="/campaigns" className="mb-20">
        &larr; Back to Campaign List
      </Link>

      <div className="profile-page">
        <div className="profile-sidebar">
          {/* Card thông tin tổng quan */}
          <div className="card">
            {/* 2. Thêm .card-body và căn giữa cho card thông tin chính */}
            <div className="card-body" style={{ textAlign: 'center' }}>
              <div className="profile-avatar">
                <span>📢</span>
              </div>
              <h3>{campaign.name}</h3>
              <div className="card-status">
                <span className={`status status-${campaign.is_active ? 'active' : 'inactive'}`}>
                  {campaign.is_active ? 'Active' : 'Inactive'}
                </span>
              </div>
            </div>
          </div>
          {/* Card thông tin chi tiết */}
          <div className="card">
            {/* 3. Thêm .card-header và .card-body cho card chi tiết */}
            <div className="card-header">
              <h4>Details</h4>
            </div>
            <div className="card-body">
              <div className="profile-details">
                <div className="detail-row">
                  <div className="detail-label">Description</div>
                  <div className="detail-value">{campaign.description || 'N/A'}</div>
                </div>
                <div className="detail-row">
                  <div className="detail-label">Start Date</div>
                  <div className="detail-value">
                    {new Date(campaign.start_date).toLocaleDateString('vi-VN')}
                  </div>
                </div>
                <div className="detail-row">
                  <div className="detail-label">End Date</div>
                  <div className="detail-value">
                    {new Date(campaign.end_date).toLocaleDateString('vi-VN')}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="profile-main">
          <div className="card">
            <div className="card-header">
              <h4>Stores in Campaign ({assignedStores.length})</h4>
              <button className='btn btn-primary' onClick={() => setIsModalOpen(true)}>
                Manage Stores
              </button>
            </div>
            <div className="card-body">
              {assignedStores.length > 0 ? (
                <ul className="card-list">
                  {assignedStores.map(store => (
                    <li key={store.id} className='card-item'>
                      <strong>{store.board_name || store.store_code}</strong> - {store.address_vi} {/* 4. Cập nhật sang store.address_vi cho nhất quán */}
                    </li>
                  ))}
                </ul>
              ) : (
                <p style={{ textAlign: 'center', padding: '20px' }}>No stores have been assigned yet.</p>
              )}
            </div>
          </div>
        </div>
      </div>
      
      <AssignStoreModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={handleSaveStores}
        campaignId={id}
        initiallyAssignedIds={assignedStores.map(s => s.id)}
      />
    </div>
  );
}

export default CampaignDetailPage;
