// src/pages/CampaignDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';
import AssignStoreModal from '../components/AssignStoreModal'; 

function CampaignDetailPage() {
  const [campaign, setCampaign] = useState(null);
  // State mới để lưu các store đã được gán
  const [assignedStores, setAssignedStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { id } = useParams();

  // State để quản lý modal
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Hàm fetch dữ liệu, sẽ được gọi lại sau khi lưu modal
  const fetchData = async () => {
    try {
      setLoading(true);
      // Dùng Promise.all để gọi 2 API cùng lúc cho hiệu quả
      const [campaignRes, storesRes] = await Promise.all([
        api.get(`/campaigns/${id}`),
        api.get(`/campaigns/${id}/stores`)
      ]);
      setCampaign(campaignRes.data.data);
      setAssignedStores(storesRes.data.data || []);
    } catch (err) {
      setError('Failed to fetch campaign details.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [id]);

  // Logic xử lý khi modal được lưu (sẽ hoàn thiện ở bước 3)
  const handleSaveStores = () => {
    setIsModalOpen(false);
    // Sau khi lưu, fetch lại dữ liệu mới nhất
    fetchData();
  };


  if (loading) return <div>Loading campaign...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;
  if (!campaign) return <div>Campaign not found.</div>;

  return (
    <div>
      <Link to="/campaigns" className="mb-20" style={{ display: 'block' }}>
        &larr; Back to Campaign List
      </Link>

      <div className="profile-page">
        {/* --- Cột trái: Campaign Info (giữ nguyên) --- */}
        <div className="profile-sidebar">
          <div className="card">
            <div className="profile-avatar">
              <span>📢</span>
            </div>
            <h3>{campaign.name}</h3>
            <div className="detail-value">
              <span className={`status status-${campaign.is_active ? 'active' : 'inactive'}`}>
                {campaign.is_active ? 'Active' : 'Inactive'}
              </span>
            </div>
          </div>
        </div>

        {/* --- Cột phải: Campaign Details & Assigned Stores --- */}
        <div className="profile-main">
          {/* Card thông tin chi tiết (giữ nguyên) */}
          <div className="card">
             {/* ... phần details giữ nguyên ... */}
          </div>

          {/* --- PHẦN MỚI: Bảng các store đã gán --- */}
          <div className="card" style={{ marginTop: '20px' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '15px 20px', borderBottom: '1px solid #eee' }}>
              <h4>Stores in Campaign ({assignedStores.length})</h4>
              <button onClick={() => setIsModalOpen(true)}>
                Manage Stores
              </button>
            </div>
            {assignedStores.length > 0 ? (
              <ul style={{ listStyle: 'none', padding: '10px 20px' }}>
                {assignedStores.map(store => (
                  <li key={store.id} style={{ padding: '10px 0', borderBottom: '1px solid #f5f5f5' }}>
                    <strong>{store.board_name || store.store_code}</strong> - {store.address_vi}
                  </li>
                ))}
              </ul>
            ) : (
              <p style={{ textAlign: 'center', padding: '20px' }}>No stores have been assigned yet.</p>
            )}
          </div>
        </div>
      </div>
      
      {/* --- PHẦN MỚI: Render Modal (sẽ hoàn thiện ở bước 3) --- */}
      {/* <AssignStoreModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={handleSaveStores}
        campaignId={id}
        initiallyAssignedIds={assignedStores.map(s => s.id)}
      /> */}
    </div>
  );
}

export default CampaignDetailPage;
