import React, { useState, useEffect, useCallback } from "react";
import api from "../services/api";
import StoreDetailModal from "../components/StoreDetailModal";
import DataTable from "../components/DataTable";
import LoadingMessage from "../components/LoadingMessage";
import ErrorMessage from "../components/ErrorMessage";
import FilterBar from "../components/FilterBar";

function StoreListPage() {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedStore, setSelectedStore] = useState(null);

  // filter state
  const [filters, setFilters] = useState({
    chain: "",
    search: "",
  });

  const fetchStores = useCallback(async () => {
    try {
      setLoading(true);
      setError("");
      const response = await api.get("/stores");
      setStores(response.data.data);
    } catch {
      setError("Failed to fetch stores.");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchStores();
  }, [fetchStores]);

  const handleOpenModal = (store) => {
    setSelectedStore(store);
    setIsModalOpen(true);
  };

  // filter logic
  const filteredStores = stores.filter((store) => {
    const matchChain = filters.chain ? store.board_name === filters.chain : true;
    const matchSearch =
      (store.board_name || "")
        .toLowerCase()
        .includes(filters.search.toLowerCase()) ||
      (store.store_code || "")
        .toLowerCase()
        .includes(filters.search.toLowerCase()) ||
      (store.address || "")
        .toLowerCase()
        .includes(filters.search.toLowerCase());

    return matchChain && matchSearch;
  });

  return (
    <div className="container">
      <div className="page-header flex-between">
        <h2 className="page-title">Store Management</h2>
      </div>

      {/* FilterBar */}
      <FilterBar
        filters={[
          {
            id: "chain",
            type: "select",
            label: "Chain",
            value: filters.chain,
            options: [
              { value: "Pharmacity", label: "Pharmacity" },
              { value: "SatraFood", label: "SatraFood" },
            ],
          },
          {
            id: "search",
            type: "text",
            label: "Search",
            value: filters.search,
            placeholder: "Name, code, or address...",
          },
        ]}
        onChange={(id, value) => setFilters((prev) => ({ ...prev, [id]: value }))}
      />

      {loading && <LoadingMessage message="Loading stores..." />}
      <ErrorMessage message={error} />

      {!loading && !error && (
        <DataTable
          columns={[
            { label: "Store Chain" },
            { label: "Store Code" },
            { label: "Address" },
            { label: "District" },
            { label: "Status" },
            { label: "Actions", className: "text-center" },
          ]}
          data={filteredStores}
          renderRow={(store) => (
            <tr key={store.id}>
              <td>{store.board_name}</td>
              <td>{store.store_code}</td>
              <td>{store.address}</td>
              <td>{store.district}</td>
              <td>
                <span
                  className={`status ${
                    store.is_active ? "status-active" : "status-inactive"
                  }`}
                >
                  {store.is_active ? "Active" : "Inactive"}
                </span>
              </td>
              <td className="table-actions">
                <button
                  className="btn btn-outline btn-sm"
                  onClick={() => handleOpenModal(store)}
                >
                  View
                </button>
              </td>
            </tr>
          )}
        />
      )}

      <StoreDetailModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        store={selectedStore}
      />
    </div>
  );
}

export default StoreListPage;
