// src/components/AssignStoreModal.jsx
import React, { useState, useEffect } from "react";
import Modal from "../Modal";
import api from "../../services/api";

function AssignStoreModal({ isOpen, onClose, onSave, campaignId, initiallyAssignedIds }) {
  const [allStores, setAllStores] = useState([]);
  const [selectedStoreIds, setSelectedStoreIds] = useState(new Set());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    if (!isOpen) return;
    const fetchStores = async () => {
      setLoading(true);
      setError("");
      setSearchTerm("");
      try {
        const res = await api.get("/stores");
        setAllStores(res.data.data || []);
        setSelectedStoreIds(new Set(initiallyAssignedIds));
      } catch {
        setError("Không thể tải danh sách cửa hàng.");
      } finally {
        setLoading(false);
      }
    };
    fetchStores();
  }, [isOpen, initiallyAssignedIds]);

  const toggleStore = (id) => {
    setSelectedStoreIds((prev) => {
      const newSet = new Set(prev);
      newSet.has(id) ? newSet.delete(id) : newSet.add(id);
      return newSet;
    });
  };

  const handleSave = async () => {
    const originalIds = new Set(initiallyAssignedIds);
    const newIds = selectedStoreIds;

    const idsToAdd = [...newIds].filter((id) => !originalIds.has(id));
    const idsToRemove = [...originalIds].filter((id) => !newIds.has(id));

    setLoading(true);
    setError("");
    try {
      await Promise.all([
        ...idsToAdd.map((store_id) => api.post(`/campaigns/${campaignId}/stores`, { store_id })),
        ...idsToRemove.map((store_id) => api.delete(`/campaigns/${campaignId}/stores/${store_id}`)),
      ]);
      onSave();
    } catch {
      setError("Lưu thay đổi thất bại.");
    } finally {
      setLoading(false);
    }
  };

  const filteredStores = allStores.filter((store) => {
    const text = `${store.board_name || ""} ${store.store_code || ""} ${store.address || ""}`.toLowerCase();
    return text.includes(searchTerm.toLowerCase());
  });

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div className="modal-header">
        <h3>Phân công cửa hàng cho chiến dịch</h3>
      </div>

      <div className="modal-content">
        {error && <p className="alert alert-error">{error}</p>}

        <div className="form-group">
          <input
            type="text"
            placeholder="Tìm kiếm chuỗi, mã, địa chỉ..."
            className="form-control"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="store-list">
          {loading && !allStores.length ? (
            <p className="empty-state-message">Đang tải danh sách cửa hàng...</p>
          ) : filteredStores.length > 0 ? (
            filteredStores.map((store) => (
              <label key={store.id} className="store-item">
                <input
                  type="checkbox"
                  checked={selectedStoreIds.has(store.id)}
                  onChange={() => toggleStore(store.id)}
                />
                <span className="store-label">
                  <strong>{store.store_code}</strong> - {store.address}
                </span>
              </label>
            ))
          ) : (
            <p className="empty-state-message">Không tìm thấy cửa hàng nào.</p>
          )}
        </div>
      </div>

      <div className="modal-actions">
        <button className="btn btn-danger btn-sm" onClick={onClose} disabled={loading}>
          Hủy
        </button>
        <button className="btn btn-primary btn-sm" onClick={handleSave} disabled={loading}>
          {loading ? "Đang lưu..." : "Lưu thay đổi"}
        </button>
      </div>
    </Modal>
  );
}

export default AssignStoreModal;
