import React, { useState, memo } from 'react';
import { GoogleMap, useJsApiLoader, InfoWindow, Marker } from '@react-google-maps/api';

const MAP_CONTAINER_STYLE = {
  width: '100%',
  height: '600px',
  borderRadius: '8px',
};

const DEFAULT_CENTER = { lat: 10.7769, lng: 106.7009 };

function StoresMap({ stores }) {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  });

  const [selectedStore, setSelectedStore] = useState(null);
  const [selectedBoard, setSelectedBoard] = useState("all"); // bộ lọc theo board

  if (!isLoaded) return <div>Loading Map...</div>;

  // lọc stores
  const filteredStores = selectedBoard === "all"
    ? stores
    : stores.filter(store => store.board_name === selectedBoard);

  return (
    <div>
      {/* Bộ lọc */}
      <div style={{ marginBottom: "10px" }}>
        <label><b>Lọc theo chuỗi:</b></label>{" "}
        <select
          value={selectedBoard}
          onChange={(e) => setSelectedBoard(e.target.value)}
        >
          <option value="all">Tất cả</option>
          <option value="SatraFood">SATRAFOODS</option>
          <option value="COOPFOOD">COOPFOOD</option>
          <option value="Pharmacity">PHARMACITY</option>
        </select>
      </div>

      <GoogleMap
        mapContainerStyle={MAP_CONTAINER_STYLE}
        center={DEFAULT_CENTER}
        zoom={12}
      >
        {filteredStores.map(store => (
        <Marker
          key={store.id}
          position={{
            lat: Number(store.latitude),
            lng: Number(store.longitude)
          }}
          onClick={() => setSelectedStore(store)}
        />
        ))}

        {selectedStore && (
          <InfoWindow
            position={{
              lat: Number(selectedStore.latitude),
              lng: Number(selectedStore.longitude)
            }}
            onCloseClick={() => setSelectedStore(null)}
          >
            <div className='popupstoredetail'>
              <h3>{selectedStore.store_code || selectedStore.name}</h3>
              <p>{selectedStore.address}</p>
              <p><b>Board:</b> {selectedStore.board_name}</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </div>
  );
}

export default memo(StoresMap);
