import React, { useState, useEffect, useCallback } from 'react'; // 1. Thêm useCallback
import { Link } from 'react-router-dom';
import api from '../services/api';
import UserForm from '../components/UserForm';

function UserListPage() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentUser, setCurrentUser] = useState(null);

    // 2. Sử dụng useCallback để ổn định hàm fetchUsers
    const fetchUsers = useCallback(async () => {
        try {
            setLoading(true);
            const response = await api.get('/users');
            setUsers(response.data.data);
        } catch (err) {
            setError('Failed to fetch users.');
        } finally {
            setLoading(false);
        }
    }, []); // Dependency rỗng vì hàm này không phụ thuộc vào props hay state nào

    useEffect(() => {
        fetchUsers();
    }, [fetchUsers]);

    const handleOpenCreateModal = () => {
        setCurrentUser(null);
        setIsModalOpen(true);
    };

    const handleOpenEditModal = (user) => {
        setCurrentUser(user);
        setIsModalOpen(true);
    };
    
    // Logic của handleSaveUser và handleDeactivateUser giữ nguyên
    const handleSaveUser = async (userData) => {
        try {
            if (userData.id) {
                const { id, ...updateData } = userData;
                const response = await api.put(`/users/${id}`, updateData);
                setUsers(users.map(u => u.id === id ? response.data.data : u));
            } else {
                const response = await api.post('/users', userData);
                setUsers([response.data.data, ...users]);
            }
            setIsModalOpen(false);
        } catch (err) {
            setError(userData.id ? 'Failed to update user.' : 'Failed to create user.');
        }
    };
    
    const handleDeactivateUser = async (user) => {
        if (window.confirm(`Are you sure you want to deactivate user ${user.full_name}?`)) {
            try {
                // Thay vì xóa cứng, chúng ta nên "hủy kích hoạt"
                const updatedUser = { ...user, is_active: false };
                const response = await api.put(`/users/${user.id}`, updatedUser);
                setUsers(users.map(u => u.id === user.id ? response.data.data : u));
            } catch (err) {
                setError('Failed to deactivate user.');
            }
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div style={{color: 'red'}}>{error}</div>;

    return (
        <div className="container">
            {/* 3. Sử dụng .page-header để thay thế cho float */}
            <div className="page-header">
                <h2>User Management</h2>
                <button onClick={handleOpenCreateModal} className="btn btn-primary">
                    + Add New User
                </button>
            </div>
            
            <div className="table-wrapper">
                {/* 4. Bỏ className="styled-table" vì .table-wrapper đã style cho table con */}
                <table>
                    <thead>
                        <tr>
                            <th>Full Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Status</th>
                            <th style={{textAlign: 'center'}}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td>
                                    <Link to={`/users/${user.id}`}>
                                        {user.full_name}
                                    </Link>
                                </td>
                                <td>{user.email}</td>
                                <td>{user.role}</td>
                                <td>
                                    <span className={`status status-${user.is_active ? 'active' : 'inactive'}`}>
                                        {user.is_active ? 'Active' : 'Inactive'}
                                    </span>
                                </td>
                                <td className="actions-cell">
                                    <button className="btn btn-outline" onClick={() => handleOpenEditModal(user)}>
                                        Edit
                                    </button>
                                    {user.is_active && (
                                        <button className="btn btn-danger" onClick={() => handleDeactivateUser(user)}>
                                            Deactivate
                                        </button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            
            <UserForm
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSave={handleSaveUser}
                initialData={currentUser}
            />
        </div>
    );
}

export default UserListPage;
