import React, { useState, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import api from "../services/api";
import DataTable from "../components/DataTable";
import LoadingMessage from "../components/LoadingMessage";
import ErrorMessage from "../components/ErrorMessage";
import StoreForm from "../components/StoreForm";

function StoreListPage() {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentStore, setCurrentStore] = useState(null);

  // Fetch stores
  const fetchStores = useCallback(async () => {
    try {
      setLoading(true);
      setError("");
      const response = await api.get("/stores");
      setStores(response.data.data);
    } catch (err) {
      console.error("❌ Fetch stores error:", err);
      setError("Failed to fetch stores.");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchStores();
  }, [fetchStores]);

  // Modal handlers
  const handleOpenCreateModal = () => {
    setCurrentStore(null);
    setIsModalOpen(true);
  };

  const handleOpenEditModal = (store) => {
    setCurrentStore(store);
    setIsModalOpen(true);
  };

  // Save store
  const handleSaveStore = async (storeData) => {
    try {
      if (storeData.id) {
        const { id, ...updateData } = storeData;
        const response = await api.put(`/stores/${id}`, updateData);
        setStores((prev) => prev.map((s) => (s.id === id ? response.data.data : s)));
      } else {
        const response = await api.post("/stores", storeData);
        setStores((prev) => [response.data.data, ...prev]);
      }
      setIsModalOpen(false);
    } catch (err) {
      console.error("❌ Save store error:", err);
      setError(storeData.id ? "Failed to update store." : "Failed to create store.");
    }
  };

  // Delete store
  const handleDeleteStore = async (store) => {
    if (!window.confirm(`Are you sure you want to delete store "${store.name}"?`)) return;
    try {
      await api.delete(`/stores/${store.id}`);
      setStores((prev) => prev.filter((s) => s.id !== store.id));
    } catch (err) {
      console.error("❌ Delete store error:", err);
      setError("Failed to delete store.");
    }
  };

  return (
    <div className="container">
      {/* Header */}
      <div className="page-header flex-between">
        <h2 className="page-title">Store Management</h2>
        <button className="btn btn-primary" onClick={handleOpenCreateModal}>
          + Add New Store
        </button>
      </div>

      {/* Loading & Error */}
      {loading && <LoadingMessage message="Loading stores..." />}
      <ErrorMessage message={error} />

      {/* Table */}
      {!loading && !error && (
        <DataTable
          columns={[
            { label: "Name" },
            { label: "Address" },
            { label: "Province" },
            { label: "District" },
            { label: "Status" },
            { label: "Actions", className: "text-center" },
          ]}
          data={stores}
          renderRow={(store) => (
            <tr key={store.id}>
              <td>
                <Link to={`/stores/${store.id}`} className="link">
                  {store.name}
                </Link>
              </td>
              <td className="truncate-text" title={store.address}>
                {store.address}
              </td>
              <td>{store.province}</td>
              <td>{store.district}</td>
              <td>
                <span
                  className={`status-badge ${
                    store.is_active ? "status-active" : "status-inactive"
                  }`}
                >
                  {store.is_active ? "Active" : "Inactive"}
                </span>
              </td>
              <td className="actions-cell">
                <button
                  className="btn btn-outline btn-sm"
                  onClick={() => handleOpenEditModal(store)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDeleteStore(store)}
                >
                  Delete
                </button>
              </td>
            </tr>
          )}
        />
      )}

      {/* Modal Form */}
      <StoreForm
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={handleSaveStore}
        initialData={currentStore}
      />
    </div>
  );
}

export default StoreListPage;
