// src/pages/CampaignDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';

function CampaignDetailPage() {
  const [campaign, setCampaign] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { id } = useParams();

  useEffect(() => {
    const fetchCampaign = async () => {
      try {
        const res = await api.get(`/campaigns/${id}`);
        setCampaign(res.data.data);
      } catch (err) {
        setError('Failed to fetch campaign details.');
      } finally {
        setLoading(false);
      }
    };
    fetchCampaign();
  }, [id]);

  const formatDate = (date) => {
    if (!date) return '—';
    return new Date(date).toLocaleString('vi-VN');
  };

  if (loading) return <div>Loading campaign...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;
  if (!campaign) return <div>Campaign not found.</div>;

  return (
    <div>
      <Link to="/campaigns" className="mb-20" style={{ display: 'block' }}>
        &larr; Back to Campaign List
      </Link>

      <div className="profile-page">
        {/* --- Sidebar --- */}
        <div className="profile-sidebar">
          <div className="card">
            <div className="profile-avatar">
              <span>📢</span>
            </div>
            <h3>{campaign.name}</h3>
            <p className="text-muted">{campaign.status}</p>
          </div>
        </div>

        {/* --- Main content --- */}
        <div className="profile-main">
          <div className="card">
            <div className="profile-details">
              <div className="detail-row">
                <div className="detail-label">ID</div>
                <div className="detail-value">{campaign.id}</div>
              </div>
              <div className="detail-row">
                <div className="detail-label">Name</div>
                <div className="detail-value">{campaign.name}</div>
              </div>
              <div className="detail-row">
                <div className="detail-label">Description</div>
                <div className="detail-value">{campaign.description || '—'}</div>
              </div>
              <div className="detail-row">
                <div className="detail-label">Status</div>
                <div className="detail-value">{campaign.status}</div>
              </div>
              <div className="detail-row">
                <div className="detail-label">Start Date</div>
                <div className="detail-value">{formatDate(campaign.start_date)}</div>
              </div>
              <div className="detail-row">
                <div className="detail-label">End Date</div>
                <div className="detail-value">{formatDate(campaign.end_date)}</div>
              </div>
              <div className="detail-row">
                <div className="detail-label">Created At</div>
                <div className="detail-value">{formatDate(campaign.created_at)}</div>
              </div>
              <div className="detail-row">
                <div className="detail-label">Updated At</div>
                <div className="detail-value">{formatDate(campaign.updated_at)}</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CampaignDetailPage;
