import React, { useState, useEffect, useCallback } from 'react'; // 1. Thêm useCallback
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';

function UserProfilePage() {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { id } = useParams();

    // 2. Sử dụng useCallback để ổn định hàm fetchUser
    const fetchUser = useCallback(async () => {
        try {
            setLoading(true);
            const response = await api.get(`/users/${id}`);
            setUser(response.data.data);
        } catch (err) {
            setError('Failed to fetch user profile.');
        } finally {
            setLoading(false);
        }
    }, [id]);

    useEffect(() => {
        fetchUser();
    }, [fetchUser]); // 3. useEffect giờ phụ thuộc vào fetchUser

    if (loading) return <div>Loading profile...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;
    if (!user) return <div>User not found.</div>;

    return (
        <div>
            <Link to="/users" className="mb-20">&larr; Back to User List</Link>
            
            <div className="profile-page">
                {/* --- Cột trái: Thông tin cơ bản & Avatar --- */}
                <div className="profile-sidebar">
                    {/* 4. Áp dụng cấu trúc .card-body cho card bên trái */}
                    <div className="card">
                        <div className="card-body" style={{textAlign: 'center'}}>
                            <div className="profile-avatar">
                                <span>AVT</span>
                            </div>
                            <h3>{user.full_name}</h3>
                            <div className="card-status">
                                <p className="text-muted">{user.role}</p>
                            </div>
                        </div>
                    </div>
                    <div className="card">
                        <div className="card-header">
                            <h4>Account Details</h4>
                        </div>
                        <div className="card-body">
                            <div className="profile-details">
                                <div className="detail-row">
                                    <div className="detail-label">Full Name</div>
                                    <div className="detail-value">{user.full_name}</div>
                                </div>
                                <div className="detail-row">
                                    <div className="detail-label">Email</div>
                                    <div className="detail-value">{user.email}</div>
                                </div>
                                <div className="detail-row">
                                    <div className="detail-label">Phone Number</div>
                                    <div className="detail-value">{user.phone_number || 'N/A'}</div>
                                </div>
                                <div className="detail-row">
                                    <div className="detail-label">Status</div>
                                    <div className="detail-value">
                                        <span className={`status status-${user.is_active ? 'active' : 'inactive'}`}>
                                            {user.is_active ? 'Active' : 'Inactive'}
                                        </span>
                                    </div>
                                </div>
                                <div className="detail-row">
                                    <div className="detail-label">Joined Date</div>
                                    <div className="detail-value">{new Date(user.created_at).toLocaleDateString('vi-VN')}</div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* --- Cột phải: Thông tin chi tiết --- */}
                <div className="profile-main">
                    {/* 5. Thêm .card-header và .card-body cho card bên phải */}
                </div>
            </div>
        </div>
    );
}

export default UserProfilePage;
