import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';

function LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    
    const { login } = useAuth();
    const navigate = useNavigate();
    const location = useLocation();
    const from = location.state?.from?.pathname || "/";

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await api.post('/auth/login', { email, password });
            login(response.data);
            navigate(from, { replace: true });
        } catch (err) {
            const errorMessage = err.response?.data?.message || 'Login failed. Please check your credentials.';
            setError(errorMessage);
        }
    };

    return (
        // 1. Sử dụng container mới để căn giữa
        <div className='login-page-container'>
            {/* 2. Bọc form trong component .card để có giao diện nhất quán */}
            <div className="card login-card">
                <div className="card-body">
                    <h2 style={{ textAlign: 'center', marginBottom: '24px' }}>Admin Login</h2>
                    <form onSubmit={handleSubmit}>
                        <div className='form-group'>
                            <label htmlFor="email">Email:</label>
                            {/* 3. Áp dụng class .form-control */}
                            <input 
                                id="email"
                                type="email" 
                                value={email} 
                                onChange={(e) => setEmail(e.target.value)} 
                                className="form-control"
                                required 
                            />
                        </div>
                        <div className='form-group'>
                            <label htmlFor="password">Password:</label>
                            {/* 3. Áp dụng class .form-control */}
                            <input 
                                id="password"
                                type="password" 
                                value={password} 
                                onChange={(e) => setPassword(e.target.value)} 
                                className="form-control"
                                required 
                            />
                        </div>

                        {/* 4. Sử dụng class .error-message */}
                        {error && <p className="error-message">{error}</p>}

                        {/* 5. Thêm class .btn-block để nút dài hết form */}
                        <button type="submit" className="btn btn-primary btn-block">
                            Login
                        </button>
                    </form>
                </div>
            </div>
        </div>  
    );
}

export default LoginPage;
