import React, { useState } from 'react';
// THAY ĐỔI 1: Import AdvancedMarker thay vì Marker
import { GoogleMap, useJsApiLoader, AdvancedMarker, InfoWindow } from '@react-google-maps/api';

const mapContainerStyle = {
  width: '100%',
  height: '500px',
  borderRadius: '8px',
};

const defaultCenter = {
  lat: 10.7769,
  lng: 106.7009
};

function StoresMap({ stores }) {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    // Để sử dụng AdvancedMarker, chúng ta cần bật thư viện 'marker'
    libraries: ['marker'],
  });

  const [selectedStore, setSelectedStore] = useState(null);

  if (!isLoaded) return <div>Loading Map...</div>;

  return (
    <GoogleMap
      mapContainerStyle={mapContainerStyle}
      center={defaultCenter}
      zoom={12}
      // Cần có mapId để AdvancedMarker hoạt động
      mapId="YOUR_MAP_ID" // Bạn có thể tạo Map ID trong Google Cloud Console
    >
      {stores.map((store) => (
        // THAY ĐỔI 2: Sử dụng component <AdvancedMarker />
        <AdvancedMarker
          key={store.id}
          position={{
            lat: parseFloat(store.latitude),
            lng: parseFloat(store.longitude)
          }}
          onClick={() => {
            setSelectedStore(store);
          }}
        />
      ))}

      {selectedStore && (
        <InfoWindow
          position={{
            lat: parseFloat(selectedStore.latitude),
            lng: parseFloat(selectedStore.longitude)
          }}
          onCloseClick={() => {
            setSelectedStore(null);
          }}
        >
          <div>
            <h4>{selectedStore.display_name || selectedStore.name}</h4>
            <p>{selectedStore.address}</p>
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
}

export default React.memo(StoresMap);
