import React from 'react';
import Modal from './Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt } from '@fortawesome/free-solid-svg-icons';

const detailStyles = {
  container: {
    padding: '10px'
  },
  row: {
    marginBottom: '12px',
  },
  label: {
    fontWeight: 'bold',
    color: '#555',
    display: 'block',
  },
  value: {
    marginLeft: '5px',
  }
};

function StoreDetailModal({ isOpen, onClose, store }) {
  if (!isOpen || !store) {
    return null;
  }

  const googleMapsUrl = `https://www.google.com/maps?q=${store.latitude},${store.longitude}`;
  const googleMapsEmbedUrl = `https://www.google.com/maps/embed/v1/place?key=AIzaSyApVzA0ZNEVhj0wimLFYlc2910cL0t3PSU&center=${store.latitude},${store.longitude}&q=${store.latitude},${store.longitude}`;

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div style={detailStyles.container}>
        <h2>Store Details</h2>

        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Board Name:</span>
          <span style={detailStyles.value}>{store.board_name}</span>
        </div>

        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Store Code:</span>
          <span style={detailStyles.value}>{store.store_code}</span>
        </div>

        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Address:</span>
          <span style={detailStyles.value}>{store.address}</span>
        </div>

        <div style={detailStyles.row}>
          <span style={detailStyles.label}>District:</span>
          <span style={detailStyles.value}>{store.district}</span>
        </div>

        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Status:</span>
          <span style={{ ...detailStyles.value, color: store.is_active ? 'green' : 'red' }}>
            {store.is_active ? 'Active' : 'Inactive'}
          </span>
        </div>

        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Google Maps:</span>
          <a href={googleMapsUrl} target="_blank" rel="noopener noreferrer" style={{ textDecoration: 'none' }}>
            <button style={{ marginTop: 5, display: 'inline-flex', alignItems: 'center', gap: '8px' }}>
              <FontAwesomeIcon icon={faMapMarkerAlt} />
              View on Google Maps
            </button>
          </a>
          <iframe
            width="450"
            height="250"
            style={{ border: 0 }}
            src={googleMapsEmbedUrl}
            allowFullScreen
            referrerPolicy="no-referrer-when-downgrade"
            title="Google Maps"
          />
        </div>

        <button className="danger" onClick={onClose}>Close</button>
      </div>
    </Modal>
  );
}

export default StoreDetailModal;
