import React from 'react';
import Modal from './Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// Import các icon bạn cần
import { faTachometerAlt, faStore, faUsers, faMap, faAddressBook } from '@fortawesome/free-solid-svg-icons';


// Simple styling for the detail view
const detailStyles = {
  container: {
    padding: '10px'
  },
  row: {
    marginBottom: '12px',
  },
  label: {
    fontWeight: 'bold',
    color: '#555',
    display: 'block',
  },
  value: {
    marginLeft: '5px',
  }
};

function StoreDetailModal({ isOpen, onClose, store }) {
  // Render nothing if the modal is not open or there's no store data
  if (!isOpen || !store) {
    return null;
  }

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div style={detailStyles.container}>
        <h2>Store Details</h2>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Chain Name:</span>
          <span style={detailStyles.value}>{store.chain_name}</span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Store Code:</span>
          <span style={detailStyles.value}>{store.board_name || store.store_code}</span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Address:</span>
          <span style={detailStyles.value}>{store.address_vi}</span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>District:</span>
          <span style={detailStyles.value}>{store.district}</span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Status:</span>
          <span style={{ ...detailStyles.value, color: store.is_active ? 'green' : 'red' }}>
            {store.is_active ? 'Active' : 'Inactive'}
          </span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Link GoogleMaps:</span>
          <button style={{ marginTop: 5, gap: 8}}>
            <FontAwesomeIcon icon={faMap} />
            <a 
              href={`https://www.google.com/maps?q=${store.latitude},${store.longitude}`} 
              target="_blank" 
              rel="noopener noreferrer"
              style={{textDecoration:'None', color:'#fff'}}
            >
              Xem trên Google Maps
            </a>
          </button>
        </div>
        <button className='danger' onClick={onClose} style={{ marginTop: '20px', float: 'right', borderRadius: 'None' }}>Close</button>
      </div>
    </Modal>
  );
}

export default StoreDetailModal;
