import React, { useState, useEffect } from 'react';
import Modal from './Modal';

function UserForm({ isOpen, onClose, onSave, initialData }) {
    const [userData, setUserData] = useState({});
    
    // useEffect sẽ chạy mỗi khi modal được mở ra
    useEffect(() => {
        if (initialData) {
            // Chế độ Edit: Nạp dữ liệu của user đang có
            setUserData(initialData);
        } else {
            // Chế độ Create: Reset state về giá trị mặc định
            setUserData({ 
                full_name: '', 
                email: '', 
                password: '', 
                phone_number: '', 
                role: 'parttime', 
                is_active: true 
            });
        }
    }, [initialData, isOpen]); // Phụ thuộc vào initialData và trạng thái mở của modal

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setUserData(prevState => ({
            ...prevState,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(userData);
    };

    return (
        <Modal isOpen={isOpen} onClose={onClose}>
            <form onSubmit={handleSubmit}>
                <h2>{initialData ? 'Edit User' : 'Add New User'}</h2>

                {/* --- Các trường chung cho cả Edit và Create --- */}
                <div style={{ marginBottom: '10px' }}>
                    <label>Full Name:</label><br/>
                    <input type="text" name="full_name" value={userData.full_name || ''} onChange={handleChange} required style={{ width: '95%' }}/>
                </div>

                {/* --- Các trường chỉ hiển thị khi Create --- */}
                {!initialData && (
                    <>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Email:</label><br/>
                            <input type="email" name="email" value={userData.email || ''} onChange={handleChange} required style={{ width: '95%' }}/>
                        </div>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Password:</label><br/>
                            <input type="password" name="password" value={userData.password || ''} onChange={handleChange} required style={{ width: '95%' }}/>
                        </div>
                         <div style={{ marginBottom: '10px' }}>
                            <label>Phone Number:</label><br/>
                            <input type="tel" name="phone_number" value={userData.phone_number || ''} onChange={handleChange} style={{ width: '95%' }}/>
                        </div>
                    </>
                )}

                {/* --- Các trường chung cho cả Edit và Create --- */}
                <div style={{ marginBottom: '10px' }}>
                    <label>Role:</label><br/>
                    <select name="role" value={userData.role || 'parttime'} onChange={handleChange} style={{ width: '100%' }}>
                        <option value="parttime">Part-time</option>
                        <option value="supervisor">Supervisor</option>
                        <option value="admin">Admin</option>
                    </select>
                </div>
                <div style={{ marginBottom: '20px' }}>
                    <label>
                        <input type="checkbox" name="is_active" checked={userData.is_active || false} onChange={handleChange} />
                        Active
                    </label>
                </div>
                
                <button type="submit" style={{ marginRight: '10px' }}>Save</button>
                <button type="button" onClick={onClose}>Cancel</button>
            </form>
        </Modal>
    );
}

export default UserForm;
