import React, { useState, useEffect } from 'react';
import api from '../services/api';
import StoreDetailModal from '../components/StoreDetailModal'; // Import the new modal

function StoreListPage() {
    const [stores, setStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [filter, setFilter] = useState('');

    // --- NEW: State to manage the detail view modal ---
    const [isViewModalOpen, setIsViewModalOpen] = useState(false);
    const [selectedStore, setSelectedStore] = useState(null);

    useEffect(() => {
        fetchStores();
    }, []);

    const fetchStores = async () => {
        try {
            const response = await api.get('/stores');
            setStores(response.data.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch stores.');
        } finally {
            setLoading(false);
        }
    };

    // --- UPDATED: This function now opens the modal ---
    const handleViewDetail = (store) => {
        setSelectedStore(store);
        setIsViewModalOpen(true);
    };

    if (loading) return <div>Loading stores...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div>
            <h2>Store Management</h2>
            <div style={{ marginBottom: '20px' }}>
            <input
                type="text"
                placeholder="Search by chain, store code, or address..."
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                style={{ padding: '6px', width: '300px' }}
            />
            </div>
            <div className="table-wrapper">
                <table>
                    <thead>
                        <tr>
                            <th>CHUỖI CỬA HÀNG</th>
                            <th>MÃ CỬA HÀNG</th>
                            <th>ĐỊA CHỈ</th>
                            <th>QUẬN/HUYỆN</th>
                            <th>STATUS</th>
                            <th>ACTIONS</th>
                        </tr>
                    </thead>
                    <tbody>
                        {stores.map((store) => (
                            <tr key={store.id}>
                                <td>{store.chain_name}</td>
                                <td>{store.board_name || store.store_code}</td>
                                <td>{store.address_vi}</td>
                                <td>{store.district}</td>
                                <td style={{ color: store.is_active ? 'green' : 'red', fontWeight: 'bold' }}>
                                    {store.is_active ? 'Active' : 'Inactive'}
                                </td>
                                <td className="actions-cell">
                                    <button onClick={() => handleViewDetail(store)}>View</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* --- ADDED: The detail modal component --- */}
            <StoreDetailModal 
                isOpen={isViewModalOpen}
                onClose={() => setIsViewModalOpen(false)}
                store={selectedStore}
            />
        </div>
    );
}

export default StoreListPage;
