import React, { useState, useEffect, useCallback } from 'react'; // 1. Thêm useCallback
import api from '../services/api';
import StoreDetailModal from '../components/StoreDetailModal';

function StoreListPage() {
    const [stores, setStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [filter, setFilter] = useState('');

    const [isViewModalOpen, setIsViewModalOpen] = useState(false);
    const [selectedStore, setSelectedStore] = useState(null);

    // 2. Sử dụng useCallback để ổn định hàm fetchStores
    const fetchStores = useCallback(async () => {
        try {
            setLoading(true);
            const response = await api.get('/stores');
            setStores(response.data.data);
        } catch (err) {
            console.error(err);
            setError('Failed to fetch stores.');
        } finally {
            setLoading(false);
        }
    }, []); // Dependency rỗng vì không phụ thuộc state/props

    useEffect(() => {
        fetchStores();
    }, [fetchStores]);

    const filteredStores = stores.filter(store =>
        (store.board_name || '').toLowerCase().includes(filter.toLowerCase()) ||
        (store.store_code || '').toLowerCase().includes(filter.toLowerCase()) ||
        (store.address || '').toLowerCase().includes(filter.toLowerCase())
    );

    const handleViewDetail = (store) => {
        setSelectedStore(store);
        setIsViewModalOpen(true);
    };

    if (loading) return <div>Loading stores...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div className="container">
            {/* 3. Sử dụng .page-header để chứa tiêu đề và thanh tìm kiếm */}
            <div className="page-header">
                <h2>Store Management</h2>
                <div className="search-bar-container" style={{ margin: 0 }}>
                    <select className="form-control" value={filter} onChange={(e) => setFilter(e.target.value)}>
                        <option value="">All</option>
                        <option value="active">Active</option>
                        <option value="inactive">Inactive</option>
                    </select>
                    <div>
                        <input
                            type="text"
                            placeholder="Search by name, code, or address..."
                            value={filter}
                            onChange={(e) => setFilter(e.target.value)}
                            className="form-control" // 4. Áp dụng class .form-control
                        />
                    </div>
                </div>
                <div className="search-bar-container" style={{ margin: 0 }}>

                </div>
            </div>

            <div className="table-wrapper">
                <table>
                    <thead>
                        <tr>
                            <th>Store Chain</th>
                            <th>Store Code</th>
                            <th>Address</th>
                            <th>District</th>
                            <th>Status</th>
                            <th style={{textAlign: 'center'}}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredStores.length === 0 ? (
                            <tr>
                                <td colSpan="6" style={{ textAlign: 'center', padding: '20px' }}>
                                    No stores found.
                                </td>
                            </tr>
                        ) : (
                            filteredStores.map((store) => (
                                <tr key={store.id}>
                                    <td>{store.board_name}</td>
                                    <td>{store.store_code}</td>
                                    {/* Bỏ class address-cell không cần thiết */}
                                    <td>{store.address}</td>
                                    <td>{store.district}</td>
                                    <td className='status-cell'>
                                        {/* 5. Sử dụng hệ thống class .status */}
                                        <span className={`status status-${store.is_active ? 'active' : 'inactive'}`}>
                                            {store.is_active ? 'Active' : 'Inactive'}
                                        </span>
                                    </td>
                                    <td className="actions-cell">
                                        <button className='btn btn-outline' onClick={() => handleViewDetail(store)}>View</button>
                                    </td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>

            <StoreDetailModal 
                isOpen={isViewModalOpen}
                onClose={() => setIsViewModalOpen(false)}
                store={selectedStore}
            />
        </div>
    );
}

export default StoreListPage;
