import React, { useState, useEffect } from 'react';
import Modal from './Modal';

// 1. Đưa defaultState ra ngoài component.
// Giờ đây nó là một hằng số ổn định và chỉ được tạo một lần.
const defaultState = {
    name: '',
    description: '',
    start_date: '',
    end_date: '',
    is_active: true,
};

function CampaignForm({ isOpen, onClose, onSave, initialData }) {
    const [campaignData, setCampaignData] = useState(defaultState);

    useEffect(() => {
        if (isOpen) {
            if (initialData) {
                const formattedData = {
                    ...initialData,
                    start_date: initialData.start_date ? new Date(initialData.start_date).toISOString().split('T')[0] : '',
                    end_date: initialData.end_date ? new Date(initialData.end_date).toISOString().split('T')[0] : '',
                };
                setCampaignData(formattedData);
            } else {
                // Sử dụng hằng số đã được định nghĩa ở ngoài
                setCampaignData(defaultState);
            }
        }
    // 2. Mảng dependency bây giờ đã chính xác, không cần thêm defaultState.
    }, [initialData, isOpen]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setCampaignData((prev) => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(campaignData);
    };

    return (
        <Modal isOpen={isOpen} onClose={onClose}>
            {/* Sử dụng modal-body để có padding và khả năng cuộn */}
            <div className="modal-body">
                <form className='modal-form' onSubmit={handleSubmit}>
                    <h3 style={{ marginBottom: '24px' }}>{initialData ? 'Edit Campaign' : 'Add New Campaign'}</h3>

                    {/* 2. Áp dụng cấu trúc .form-group và .form-control */}
                    <div className="form-group">
                        <label htmlFor="name">Campaign Name:</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={campaignData.name || ''}
                            onChange={handleChange}
                            className="form-control"
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="description">Description:</label>
                        <textarea
                            id="description"
                            name="description"
                            value={campaignData.description || ''}
                            onChange={handleChange}
                            rows={4}
                            className="form-control"
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="start_date">Start Date:</label>
                        <input
                            type="date"
                            id="start_date"
                            name="start_date"
                            value={campaignData.start_date || ''}
                            onChange={handleChange}
                            className="form-control"
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="end_date">End Date:</label>
                        <input
                            type="date"
                            id="end_date"
                            name="end_date"
                            value={campaignData.end_date || ''}
                            onChange={handleChange}
                            className="form-control"
                            required
                        />
                    </div>
                    
                    {/* 3. Chuyển đổi Status select thành checkbox */}
                    <div className="form-group" style={{ display: 'flex', alignItems: 'center' }}>
                         <input 
                            type="checkbox" 
                            id="is_active"
                            name="is_active" 
                            checked={campaignData.is_active} 
                            onChange={handleChange}
                            style={{ marginRight: '10px', width: 'auto' }}
                         />
                        <label htmlFor="is_active" style={{ marginBottom: 0, fontWeight: 'normal' }}>
                           Campaign is Active
                        </label>
                    </div>

                    {/* 4. Cập nhật buttons với hệ thống .btn */}
                    <div className="modal-actions">
                        <button type="button" onClick={onClose} className="btn btn-danger">
                            Cancel
                        </button>
                        <button type="submit" className="btn btn-primary">
                            Save
                        </button>
                    </div>
                </form>
            </div>
        </Modal>
    );
}

export default CampaignForm;
