import React, { createContext, useState, useContext, useEffect } from 'react';
import api from '../services/api';
import { loginService, logoutService, getProfileService } from '../services/authService';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  const setAuthToken = (token) => {
    if (token) {
      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
      delete api.defaults.headers.common['Authorization'];
    }
  };

  // Load lại user nếu có token
  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');

    if (storedToken && storedUser) {
      setToken(storedToken);
      setUser(JSON.parse(storedUser));
      setAuthToken(storedToken);
      setIsAuthenticated(true);
    }

    setLoading(false);
  }, []);

  const login = async (credentials) => {
    const data = await loginService(credentials);
    localStorage.setItem('token', data.token);
    localStorage.setItem('user', JSON.stringify(data.user));

    setUser(data.user);
    setToken(data.token);
    setIsAuthenticated(true);
    setAuthToken(data.token);
  };

  const logout = async () => {
    await logoutService(); // Nếu API không cần logout thì có thể bỏ
    localStorage.removeItem('token');
    localStorage.removeItem('user');

    setUser(null);
    setToken(null);
    setIsAuthenticated(false);
    setAuthToken(null);
  };

  const fetchProfile = async () => {
    const profile = await getProfileService();
    setUser(profile.user);
    localStorage.setItem('user', JSON.stringify(profile.user));
  };

  const value = { user, token, isAuthenticated, loading, login, logout, fetchProfile };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
