import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import StoresMap from '../components/StoresMap';
// 1. Import dữ liệu từ file mới
import { districts, districtMap } from '../constants/locationData'; 

function DashBoardPage() {
    const [stores, setStores] = useState([]);
    const [campaign, setCampaign] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // 2. Sử dụng useCallback cho hàm fetch
    const fetchStores = useCallback(async () => {
        try {
            setLoading(true);
            const response = await api.get('/stores');
            const response2 = await api.get('/campaigns');
            setStores(response.data.data);
            setCampaign(response.data.data);
        } catch (err) {
            setError('Failed to load store data for the map.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchStores();
    }, [fetchStores]);
    
    // 3. Xóa các hằng số districts và districtMap đã được hardcode ở đây

    return (
        <div className="container">
            {/* 4. Áp dụng cấu trúc .page-header nhất quán */}
            <div className="page-header">
                <h2>Dashboard Overview</h2>
            </div>

            {/* 5. Áp dụng cấu trúc .card-header và .card-body */}
            <div className="card">
                <div className="card-header">
                    <h4>Store Locations</h4>
                </div>
                <div className="card-body">
                    {loading && <p>Loading map data...</p>}
                    {error && <p className="error-message">{error}</p>}
                    {!loading && !error && (
                        <StoresMap 
                            stores={stores} 
                            districts={districts} 
                            districtMap={districtMap} 
                            campaign={campaign}
                            campaignStores={campaignStores}
                        />
                    )}
                </div>
            </div>
        </div>
    );
}

export default DashBoardPage;
