import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import StoreDetailModal from '../components/StoreDetailModal';

function StoreListPage() {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filterKeyword, setFilterKeyword] = useState('');
  const [filterChain, setFilterChain] = useState('');

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedStore, setSelectedStore] = useState(null);

  const fetchStores = useCallback(async () => {
    setLoading(true);
    try {
      const response = await api.get('/stores');
      setStores(response.data.data);
    } catch {
      setError('Failed to fetch stores.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchStores();
  }, [fetchStores]);

  const filteredStores = stores.filter((store) => {
    const matchKeyword =
      (store.board_name || '').toLowerCase().includes(filterKeyword.toLowerCase()) ||
      (store.store_code || '').toLowerCase().includes(filterKeyword.toLowerCase()) ||
      (store.address || '').toLowerCase().includes(filterKeyword.toLowerCase());

    const matchChain = filterChain ? store.board_name === filterChain : true;

    return matchKeyword && matchChain;
  });

  const openModal = (store) => {
    setSelectedStore(store);
    setIsModalOpen(true);
  };

  const StoreRow = ({ store }) => (
    <tr>
      <td>{store.board_name}</td>
      <td>{store.store_code}</td>
      <td>{store.address}</td>
      <td>{store.district}</td>
      <td>
        <span className={`status ${store.is_active ? 'status-active' : 'status-inactive'}`}>
          {store.is_active ? 'Active' : 'Inactive'}
        </span>
      </td>
      <td className="text-center">
        <button className="btn btn-outline btn-sm" onClick={() => openModal(store)}>
          View
        </button>
      </td>
    </tr>
  );

  if (loading) return <div className="alert alert-info">Loading stores...</div>;
  if (error) return <div className="alert alert-error">{error}</div>;

  return (
    <div className="storelist-container">
      {/* Header */}
      <div className="page-header">
        <h2 className="page-title">Store Management</h2>
      </div>

      {/* Filterbar */}
      <div className="filterbar">
        <div className="filter-item">
          <label htmlFor="chain">Chain</label>
          <select
            id="chain"
            className="form-control"
            value={filterChain}
            onChange={(e) => setFilterChain(e.target.value)}
          >
            <option value="">All Chains</option>
            <option value="Pharmacity">Pharmacity</option>
            <option value="Satrafood">Satrafoods</option>
          </select>
        </div>

        <div className="filter-item">
          <label htmlFor="search">Search</label>
          <input
            id="search"
            type="text"
            className="form-control"
            placeholder="Name, code, or address..."
            value={filterKeyword}
            onChange={(e) => setFilterKeyword(e.target.value)}
          />
        </div>
      </div>

      {/* Table */}
      <div className="table-wrapper">
        <table className="table">
          <thead>
            <tr>
              <th>Store Chain</th>
              <th>Store Code</th>
              <th>Address</th>
              <th>District</th>
              <th>Status</th>
              <th className="text-center">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredStores.length === 0 ? (
              <tr>
                <td colSpan="6" className="table-empty">
                  No stores found.
                </td>
              </tr>
            ) : (
              filteredStores.map((store) => <StoreRow key={store.id} store={store} />)
            )}
          </tbody>
        </table>
      </div>

      {/* Modal */}
      <StoreDetailModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        store={selectedStore}
      />
    </div>
  );
}

export default StoreListPage;
