import React, { useEffect, useRef, useState } from 'react';
import { GoogleMap, useJsApiLoader, InfoWindow } from '@react-google-maps/api';

function StoresMap({ stores }) {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ['marker'],
  });

  const mapRef = useRef(null);
  const [selectedStore, setSelectedStore] = useState(null);

  useEffect(() => {
    if (!isLoaded || !mapRef.current) return;

    stores.forEach(store => {
      const pos = { lat: parseFloat(store.latitude), lng: parseFloat(store.longitude) };

      const div = document.createElement('div');
      div.style.width = '20px';
      div.style.height = '20px';
      div.style.borderRadius = '50%';
      div.style.background = '#ff2d55';
      div.style.boxShadow = '0 0 10px rgba(255,45,85,0.7)';

      const marker = new window.google.maps.marker.AdvancedMarkerElement({
        position: pos,
        map: mapRef.current,
        content: div,
      });

      marker.addListener('click', () => setSelectedStore(store));
    });
  }, [isLoaded, stores]);

  if (!isLoaded) return <div>Loading Map...</div>;

  return (
    <GoogleMap
      mapContainerStyle={{ width: '100%', height: '500px' }}
      center={{ lat: 10.7769, lng: 106.7009 }}
      zoom={12}
      mapId="YOUR_MAP_ID"
      onLoad={(map) => (mapRef.current = map)}
    >
      {selectedStore && (
        <InfoWindow
          position={{
            lat: parseFloat(selectedStore.latitude),
            lng: parseFloat(selectedStore.longitude),
          }}
          onCloseClick={() => setSelectedStore(null)}
        >
          <div>
            <h4>{selectedStore.display_name || selectedStore.name}</h4>
            <p>{selectedStore.address}</p>
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
}

export default React.memo(StoresMap);
