import React from 'react';
import Modal from './Modal';

const detailStyles = {
  container: { padding: '10px' },
  row: { marginBottom: '12px' },
  label: { fontWeight: 'bold', color: '#555', display: 'block' },
  value: { marginLeft: '5px' }
};

function UserDetailModal({ isOpen, onClose, user }) {
  if (!isOpen || !user) {
    return null;
  }

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div style={detailStyles.container}>
        <h2>User Details</h2>
        
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Full Name:</span>
          <span style={detailStyles.value}>{user.full_name}</span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Email:</span>
          <span style={detailStyles.value}>{user.email}</span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Phone Number:</span>
          <span style={detailStyles.value}>{user.phone_number || 'N/A'}</span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Role:</span>
          <span style={{ ...detailStyles.value, textTransform: 'capitalize' }}>{user.role}</span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Status:</span>
          {/* --- IMPROVEMENT: Use status class from index.css --- */}
          <span className={`status status-${user.is_active ? 'active' : 'inactive'}`}>
            {user.is_active ? 'Active' : 'Inactive'}
          </span>
        </div>
        <div style={detailStyles.row}>
          <span style={detailStyles.label}>Joined Date:</span>
          <span style={detailStyles.value}>{new Date(user.created_at).toLocaleDateString('vi-VN')}</span>
        </div>

        {/* --- IMPROVEMENT: Use global button class --- */}
        <button className="danger" onClick={onClose} style={{ marginTop: '20px' }}>
          Close
        </button>
      </div>
    </Modal>
  );
}

export default UserDetailModal;
