// File: DashBoardPage.jsx
import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import StoresMap from '../components/StoresMap';
import { districts, districtMap } from '../constants/locationData'; 

function DashBoardPage() {
    const [stores, setStores] = useState([]);
    const [campaigns, setCampaigns] = useState([]);          // 🔹 đổi tên state
    const [campaignStores, setCampaignStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Fetch dữ liệu
    const fetchStoresAndCampaigns = useCallback(async () => {
        try {
            setLoading(true);

            const [storesRes, campaignsRes, campaignStoresRes] = await Promise.all([
                api.get('/stores'),
                api.get('/campaigns'),
                api.get('/campaigns/campaign-stores'),
            ]);

            setStores(storesRes.data.data);
            setCampaigns(campaignsRes.data.data);               // 🔹 đổi setter
            setCampaignStores(campaignStoresRes.data.data);

        } catch (err) {
            setError('Failed to load data for the map.');
            console.error("❌ Fetch error:", err);
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchStoresAndCampaigns();
    }, [fetchStoresAndCampaigns]);
    
    return (
        <div className="container">
            <div className="page-header">
                <h2>Dashboard Overview</h2>
            </div>

            <div className="card">
                <div className="card-header">
                    <h4>Store Locations</h4>
                </div>
                <div className="card-body">
                    {loading && <p>Loading map data...</p>}
                    {error && <p className="error-message">{error}</p>}
                    {!loading && !error && (
                        <StoresMap 
                            stores={stores} 
                            districts={districts} 
                            districtMap={districtMap} 
                            campaigns={campaigns}          // 🔹 truyền xuống dưới dạng plural
                            campaignStores={campaignStores}
                        />
                    )}
                </div>
            </div>
        </div>
    );
}

export default DashBoardPage;
