import React from 'react';
import { Link, Outlet } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function Layout() {
    const { user, logout } = useAuth();

    return (
        <div>
            <nav style={{ marginBottom: '20px', paddingBottom: '10px', borderBottom: '1px solid #ccc' }}>
                <Link to="/" style={{ marginRight: '15px' }}>Dashboard</Link>
                <Link to="/stores" style={{ marginRight: '15px' }}>Stores</Link>
                <Link to="/users" style={{ marginRight: '15px' }}>Users</Link> {/* Add this link */}
                <span style={{ float: 'right' }}>
                    Welcome, {user?.full_name} | <button onClick={logout}>Logout</button>
                </span>
            </nav>
            <main>
                <Outlet />
            </main>
        </div>
    );
}

export default Layout;
