import React, { useState, useEffect } from 'react';
import Modal from './Modal';

function UserForm({ isOpen, onClose, onSave, initialData }) {
    const [userData, setUserData] = useState({});
    
    useEffect(() => {
        if (isOpen) { // Chỉ reset state khi modal thực sự mở
            if (initialData) {
                setUserData(initialData);
            } else {
                setUserData({ 
                    full_name: '', 
                    email: '', 
                    password: '', 
                    phone_number: '', 
                    role: 'parttime', 
                    is_active: true 
                });
            }
        }
    }, [initialData, isOpen]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setUserData(prevState => ({
            ...prevState,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(userData);
    };

    return (
        <Modal isOpen={isOpen} onClose={onClose}>
            {/* Thêm một container để có padding chuẩn */}
            <div style={{ padding: '20px' }}>
                <form className='modal-form' onSubmit={handleSubmit}>
                    <h3 style={{ marginBottom: '20px' }}>{initialData ? 'Edit User' : 'Add New User'}</h3>

                    {/* 1. Áp dụng cấu trúc .form-group cho tất cả các trường */}
                    <div className="form-group">
                        <label htmlFor="full_name">Full Name:</label>
                        {/* 2. Thêm className="form-control" cho input */}
                        <input 
                            type="text" 
                            id="full_name"
                            name="full_name" 
                            value={userData.full_name || ''} 
                            onChange={handleChange} 
                            className="form-control"
                            required 
                        />
                    </div>

                    {/* Các trường chỉ hiển thị khi tạo mới */}
                    {!initialData && (
                        <>
                            <div className="form-group">
                                <label htmlFor="email">Email:</label>
                                <input 
                                    type="email" 
                                    id="email"
                                    name="email" 
                                    value={userData.email || ''} 
                                    onChange={handleChange} 
                                    className="form-control"
                                    required 
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="password">Password:</label>
                                <input 
                                    type="password" 
                                    id="password"
                                    name="password" 
                                    value={userData.password || ''} 
                                    onChange={handleChange} 
                                    className="form-control"
                                    required 
                                />
                            </div>
                        </>
                    )}

                    {/* 3. Cho phép chỉnh sửa SĐT ở cả 2 chế độ Create và Edit */}
                    <div className="form-group">
                        <label htmlFor="phone_number">Phone Number:</label>
                        <input 
                            type="tel" 
                            id="phone_number"
                            name="phone_number" 
                            value={userData.phone_number || ''} 
                            onChange={handleChange} 
                            className="form-control"
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="role">Role:</label>
                        <select 
                            id="role"
                            name="role" 
                            value={userData.role || 'parttime'} 
                            onChange={handleChange} 
                            className="form-control"
                        >
                            <option value="parttime">Part-time</option>
                            <option value="supervisor">Supervisor</option>
                            <option value="admin">Admin</option>
                        </select>
                    </div>

                    <div className="form-group" style={{ display: 'flex', alignItems: 'center' }}>
                         <input 
                            type="checkbox" 
                            id="is_active"
                            name="is_active" 
                            checked={userData.is_active || false} 
                            onChange={handleChange}
                            style={{ marginRight: '10px' }}
                         />
                        <label htmlFor="is_active" style={{ marginBottom: 0, fontWeight: 'normal' }}>
                           User is Active
                        </label>
                    </div>

                    {/* 4. Sử dụng .modal-actions và hệ thống .btn cho các nút */}
                    <div className="modal-actions">
                        <button type="button" onClick={onClose} className="btn btn-danger">
                            Cancel
                        </button>
                        <button type="submit" className="btn btn-primary">
                            Save
                        </button>
                    </div>
                </form>
            </div>
        </Modal>
    );
}

export default UserForm;
