import React, { useState, useEffect } from 'react';
import api from '../services/api';

function StoreListPage() {
    const [stores, setStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [newStore, setNewStore] = useState({ name: '', address: '', latitude: '', longitude: '' });

    // --- EDIT STATE ---
    const [editingStoreId, setEditingStoreId] = useState(null);
    const [editingStoreData, setEditingStoreData] = useState({});

    useEffect(() => {
        const fetchStores = async () => {
            try {
                const response = await api.get('/stores');
                setStores(response.data.data);
            } catch (err) {
                setError('Failed to fetch stores.');
            } finally {
                setLoading(false);
            }
        };
        fetchStores();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewStore(prevState => ({ ...prevState, [name]: value }));
    };

    const handleCreateStore = async (e) => {
        e.preventDefault();
        if (!newStore.name || !newStore.address || !newStore.latitude || !newStore.longitude) {
            alert('Please fill in all fields');
            return;
        }
        try {
            const response = await api.post('/stores', newStore);
            setStores([response.data.data, ...stores]);
            setNewStore({ name: '', address: '', latitude: '', longitude: '' });
        } catch (err) {
            setError('Failed to create store.');
        }
    };

    const handleDeleteStore = async (storeId) => {
        if (window.confirm('Are you sure you want to delete this store?')) {
            try {
                await api.delete(`/stores/${storeId}`);
                setStores(stores.filter(store => store.id !== storeId));
            } catch (err) {
                setError('Failed to delete store.');
            }
        }
    };

    // --- EDIT HANDLERS ---
    const handleEditClick = (store) => {
        setEditingStoreId(store.id);
        setEditingStoreData({ ...store });
    };

    const handleCancelEdit = () => {
        setEditingStoreId(null);
    };

    const handleEditInputChange = (e) => {
        const { name, value } = e.target;
        setEditingStoreData(prevState => ({ ...prevState, [name]: value }));
    };

    const handleUpdateStore = async (e) => {
        e.preventDefault();
        try {
            const response = await api.put(`/stores/${editingStoreId}`, editingStoreData);
            setStores(stores.map(store =>
                store.id === editingStoreId ? response.data.data : store
            ));
            setEditingStoreId(null);
        } catch (err) {
            setError('Failed to update store.');
        }
    };

    if (loading) return <div>Loading stores...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div>
            <h2>Store Management</h2>

            {/* --- Create Store Form --- */}
            <form onSubmit={handleCreateStore} style={{ marginBottom: '20px', paddingBottom: '20px', borderBottom: '1px solid #ccc' }}>
                <h3>Add New Store</h3>
                <input type="text" name="name" placeholder="Store Name" value={newStore.name} onChange={handleInputChange} required style={{ marginRight: '10px' }}/>
                <input type="text" name="address" placeholder="Address" value={newStore.address} onChange={handleInputChange} required style={{ marginRight: '10px' }}/>
                <input type="text" name="latitude" placeholder="Latitude" value={newStore.latitude} onChange={handleInputChange} required style={{ marginRight: '10px' }}/>
                <input type="text" name="longitude" placeholder="Longitude" value={newStore.longitude} onChange={handleInputChange} required style={{ marginRight: '10px' }}/>
                <button type="submit">Create Store</button>
            </form>

            {/* --- Table of Stores with Edit/View Modes --- */}
            <form onSubmit={handleUpdateStore}>
                <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Address</th>
                            <th>Latitude</th>
                            <th>Longitude</th>
                            <th>Status</th>
                            <th style={{width: '150px'}}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {stores.map(store => (
                            <tr key={store.id}>
                                {editingStoreId === store.id ? (
                                    <>
                                        {/* --- Edit Mode --- */}
                                        <td><input type="text" name="name" value={editingStoreData.name} onChange={handleEditInputChange} /></td>
                                        <td><input type="text" name="address" value={editingStoreData.address} onChange={handleEditInputChange} /></td>
                                        <td><input type="text" name="latitude" value={editingStoreData.latitude} onChange={handleEditInputChange} /></td>
                                        <td><input type="text" name="longitude" value={editingStoreData.longitude} onChange={handleEditInputChange} /></td>
                                        <td><input type="text" name="status" value={editingStoreData.status} onChange={handleEditInputChange} /></td>
                                        <td>
                                            <button type="submit">Save</button>
                                            <button type="button" onClick={handleCancelEdit}>Cancel</button>
                                        </td>
                                    </>
                                ) : (
                                    <>
                                        {/* --- View Mode --- */}
                                        <td>{store.name}</td>
                                        <td>{store.address}</td>
                                        <td>{store.latitude}</td>
                                        <td>{store.longitude}</td>
                                        <td>{store.status}</td>
                                        <td>
                                            <button type="button" onClick={() => handleEditClick(store)}>Edit</button>
                                            <button type="button" onClick={() => handleDeleteStore(store.id)} style={{ marginLeft: '5px' }}>Delete</button>
                                        </td>
                                    </>
                                )}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </form>
        </div>
    );
}

export default StoreListPage;
