import React, { useState } from "react";
import { GoogleMap, useJsApiLoader, Marker, InfoWindow } from "@react-google-maps/api";

const mapContainerStyle = {
  width: "100%",
  height: "800px",
  borderRadius: "8px",
};

const defaultCenter = { lat: 10.7769, lng: 106.7009 };

// Map board_name → màu/icon
const boardColors = {
  "Board A": "red",
  "Board B": "blue",
  "Board C": "green",
  default: "orange",
};

// Hàm tạo icon SVG màu tuỳ biến
const getMarkerIcon = (color) => ({
  path: "M12 2C8 2 5 5 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-4-3-7-7-7z",
  fillColor: color,
  fillOpacity: 1,
  strokeWeight: 1,
  strokeColor: "white",
  scale: 1.5,
  anchor: { x: 12, y: 24 },
});

function StoresMap({ stores }) {
  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  const [selectedStore, setSelectedStore] = useState(null);

  if (!isLoaded) return <div>Loading Map...</div>;

  return (
    <GoogleMap mapContainerStyle={mapContainerStyle} center={defaultCenter} zoom={12}>
      {stores.map((store) => {
        const color = boardColors[store.board_name] || boardColors.default;
        return (
          <Marker
            key={store.id}
            position={{
              lat: parseFloat(store.latitude),
              lng: parseFloat(store.longitude),
            }}
            icon={getMarkerIcon(color)}
            onClick={() => setSelectedStore(store)}
          />
        );
      })}

      {selectedStore && (
        <InfoWindow
          position={{
            lat: parseFloat(selectedStore.latitude),
            lng: parseFloat(selectedStore.longitude),
          }}
          onCloseClick={() => setSelectedStore(null)}
        >
          <div>
            <h4>{selectedStore.display_name || selectedStore.name}</h4>
            <p>{selectedStore.address}</p>
            <small>Board: {selectedStore.board_name}</small>
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
}

export default React.memo(StoresMap);
