import React, { useState, useEffect } from 'react';
import Modal from './Modal';

function UserForm({ isOpen, onClose, onSave, initialData }) {
    const [userData, setUserData] = useState({});
    
    useEffect(() => {
        if (initialData) {
            setUserData(initialData);
        } else {
            // Reset state cho việc tạo mới
            setUserData({ full_name: '', email: '', password: '', phone_number: '', role: 'parttime', is_active: true });
        }
    }, [initialData, isOpen]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setUserData(prevState => ({
            ...prevState,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(userData);
    };

    return (
        <Modal isOpen={isOpen} onClose={onClose}>
            <form onSubmit={handleSubmit}>
                <h2>Edit User</h2>
                <div style={{ marginBottom: '10px' }}>
                    <label>Full Name:</label><br/>
                    <input type="text" name="full_name" value={userData.full_name} onChange={handleChange} required style={{ width: '95%' }}/>
                </div>
                <div style={{ marginBottom: '10px' }}>
                    <label>Role:</label><br/>
                    <select name="role" value={userData.role} onChange={handleChange} style={{ width: '100%' }}>
                        <option value="parttime">Part-time</option>
                        <option value="supervisor">Supervisor</option>
                        <option value="admin">Admin</option>
                    </select>
                </div>
                <div style={{ marginBottom: '10px' }}>
                    <label>
                        <input type="checkbox" name="is_active" checked={userData.is_active} onChange={handleChange} />
                        Active
                    </label>
                </div>
                <button type="submit" style={{ marginRight: '10px' }}>Save Changes</button>
                <button type="button" onClick={onClose}>Cancel</button>
            </form>
        </Modal>
    );
}

export default UserForm;
