import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import StoresMap from '../components/StoresMap';
import { districts, districtMap } from '../constants/locationData';

function DashBoardPage() {
    const [stores, setStores] = useState([]);
    const [campaignStores, setCampaignStores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Fetch campaignStores
    const fetchCampaignStores = useCallback(async () => {
        try {
            const response = await api.get('/campaigns/:id/stores');
            setCampaignStores(response.data.data); // assuming response.data.data is an array of store IDs
        } catch (err) {
            setError('Failed to load campaign store data.');
            console.error(err);
        }
    }, []);

    // Fetch stores and filter by campaignStores
    const fetchStores = useCallback(async () => {
        try {
            setLoading(true);
            const response = await api.get('/stores');
            let allStores = response.data.data;
            // Filter stores by campaignStores
            if (campaignStores.length > 0) {
                allStores = allStores.filter(store =>
                    campaignStores.includes(store.id)
                );
            }
            setStores(allStores);
        } catch (err) {
            setError('Failed to load store data for the map.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    }, [campaignStores]);

    // Fetch campaignStores first, then stores
    useEffect(() => {
        fetchCampaignStores();
    }, [fetchCampaignStores]);

    useEffect(() => {
        if (campaignStores.length > 0) {
            fetchStores();
        }
    }, [campaignStores, fetchStores]);

    return (
        <div className="container">
            <div className="page-header">
                <h2>Dashboard Overview</h2>
            </div>
            <div className="card">
                <div className="card-header">
                    <h4>Store Locations</h4>
                </div>
                <div className="card-body">
                    {loading && <p>Loading map data...</p>}
                    {error && <p className="error-message">{error}</p>}
                    {!loading && !error && (
                        <StoresMap 
                            stores={stores} 
                            districts={districts} 
                            districtMap={districtMap} 
                            campaignStores={campaignStores}
                        />
                    )}
                </div>
            </div>
        </div>
    );
}

export default DashBoardPage;
