import React, { useState, useEffect, useCallback } from "react";
import api from "../services/api";
import StoreDetailModal from "../components/StoreDetailModal";
import DataTable from "../components/DataTable";
import FilterBar from "../components/FilterBar";

function StoreListPage() {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [filterKeyword, setFilterKeyword] = useState("");
  const [filterChain, setFilterChain] = useState("");

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedStore, setSelectedStore] = useState(null);

  const fetchStores = useCallback(async () => {
    setLoading(true);
    try {
      const response = await api.get("/stores");
      setStores(response.data.data);
    } catch {
      setError("Failed to fetch stores.");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchStores();
  }, [fetchStores]);

  const filteredStores = stores.filter((store) => {
    const matchKeyword =
      (store.board_name || "").toLowerCase().includes(filterKeyword.toLowerCase()) ||
      (store.store_code || "").toLowerCase().includes(filterKeyword.toLowerCase()) ||
      (store.address || "").toLowerCase().includes(filterKeyword.toLowerCase());

    const matchChain = filterChain
      ? (store.board_name || "").toLowerCase() === filterChain.toLowerCase()
      : true;

    return matchKeyword && matchChain;
  });

  const openModal = (store) => {
    setSelectedStore(store);
    setIsModalOpen(true);
  };

  const StoreRow = (store) => (
    <tr key={store.id}>
      <td>{store.board_name}</td>
      <td>{store.store_code}</td>
      <td>{store.address}</td>
      <td>{store.district}</td>
      <td>
        <span className={`status ${store.is_active ? "status-active" : "status-inactive"}`}>
          {store.is_active ? "Active" : "Inactive"}
        </span>
      </td>
      <td className="text-center">
        <button className="btn btn-outline btn-sm" onClick={() => openModal(store)}>
          View
        </button>
      </td>
    </tr>
  );

  const columns = [
    { key: "board_name", label: "Store Chain" },
    { key: "store_code", label: "Store Code" },
    { key: "address", label: "Address" },
    { key: "district", label: "District" },
    { key: "status", label: "Status" },
    { key: "actions", label: "Actions", className: "text-center" },
  ];

  return (
    <div className="container">
      {/* Header */}
      <div className="page-header">
        <h2 className="page-title">Store Management</h2>
      </div>

      {/* Filterbar */}
      <FilterBar
        filterChain={filterChain}
        setFilterChain={setFilterChain}
        filterKeyword={filterKeyword}
        setFilterKeyword={setFilterKeyword}
      />

      {/* Alerts */}
      {loading && <div className="alert alert-info">Loading stores...</div>}
      {error && <div className="alert alert-error">{error}</div>}

      {/* Table */}
      {!loading && !error && (
        <DataTable
          columns={columns}
          data={filteredStores}
          renderRow={StoreRow}
          emptyMessage="No stores found."
        />
      )}

      {/* Modal */}
      <StoreDetailModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        store={selectedStore}
      />
    </div>
  );
}

export default StoreListPage;
