import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';
import AssignStoreModal from '../components/AssignStoreModal';

function CampaignDetailPage() {
  const [campaign, setCampaign] = useState(null);
  const [assignedStores, setAssignedStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { id } = useParams();
  const [isModalOpen, setIsModalOpen] = useState(false);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const [campaignRes, storesRes] = await Promise.all([
        api.get(`/campaigns/${id}`),
        api.get(`/campaigns/${id}/stores`)
      ]);
      setCampaign(campaignRes.data.data);
      setAssignedStores(storesRes.data.data || []);
    } catch (err) {
      setError('Failed to fetch campaign details.');
    } finally {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchData();
  }, [fetchData]); 

  const handleSaveStores = () => {
    setIsModalOpen(false);
    fetchData();
  };

  if (loading) return <div className="loading-message">Loading campaign...</div>;
  if (error) return <div className="error-message">{error}</div>;
  if (!campaign) return <div className="empty-state-message">Campaign not found.</div>;

  return (
    <div className="container">
      <Link to="/campaigns" className="btn-link back-link">
        &larr; Back to Campaign List
      </Link>

      <div className="profile-page">
        {/* Sidebar */}
        <aside className="profile-sidebar">
          {/* Campaign Info */}
          <div className="card profile-card">
            <div className="card-body text-center">
              <div className="profile-avatar">ðŸ“¢</div>
              <h3 className="profile-title">{campaign.name}</h3>
              <div className="profile-status">
                <span className={`status-badge ${campaign.is_active ? 'status-active' : 'status-inactive'}`}>
                  {campaign.is_active ? 'Active' : 'Inactive'}
                </span>
              </div>
            </div>
          </div>

          {/* Campaign Details */}
          <div className="card detail-card">
            <div className="card-header">
              <h4 className="card-title">Details</h4>
            </div>
            <div className="card-body">
              <div className="detail-list">
                <div className="detail-row">
                  <div className="detail-label">Description</div>
                  <div className="detail-value">{campaign.description || 'N/A'}</div>
                </div>
                <div className="detail-row">
                  <div className="detail-label">Start Date</div>
                  <div className="detail-value">{new Date(campaign.start_date).toLocaleDateString('vi-VN')}</div>
                </div>
                <div className="detail-row">
                  <div className="detail-label">End Date</div>
                  <div className="detail-value">{new Date(campaign.end_date).toLocaleDateString('vi-VN')}</div>
                </div>
              </div>
            </div>
          </div>
        </aside>

        {/* Main Content */}
        <main className="profile-main">
          <div className="card store-card">
            <div className="card-header flex-between">
              <h4 className="card-title">Stores in Campaign ({assignedStores.length})</h4>
              <button className="btn btn-primary" onClick={() => setIsModalOpen(true)}>
                Manage Stores
              </button>
            </div>
            <div className="card-body" id="store-list">
              {assignedStores.length > 0 ? (
                <ul className="card-list">
                  {assignedStores.map(store => (
                    <li key={store.id} className="card-item">
                      <strong>{store.store_code}</strong> - {store.address}
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="empty-state-message">No stores have been assigned yet.</p>
              )}
            </div>
          </div>
        </main>
      </div>

      <AssignStoreModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={handleSaveStores}
        campaignId={id}
        initiallyAssignedIds={assignedStores.map(s => s.id)}
      />
    </div>
  );
}

export default CampaignDetailPage;
