// File: DashBoardPage.jsx
import React, { useState, useEffect, useCallback } from "react";
import api from "../services/api";
import StoresMap from "../components/StoresMap";
import LoadingMessage from "../components/LoadingMessage";
import ErrorMessage from "../components/ErrorMessage";
import { districts, districtMap } from "../constants/locationData";

function DashBoardPage() {
  const [stores, setStores] = useState([]);
  const [campaigns, setCampaigns] = useState([]);
  const [campaignStores, setCampaignStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Fetch dữ liệu
  const fetchStoresAndCampaigns = useCallback(async () => {
    try {
      setLoading(true);
      setError("");

      const [storesRes, campaignsRes, campaignStoresRes] = await Promise.all([
        api.get("/stores"),
        api.get("/campaigns"),
        api.get("/campaigns/campaign-stores"),
      ]);

      setStores(storesRes.data.data);
      setCampaigns(campaignsRes.data.data);
      setCampaignStores(campaignStoresRes.data.data);
    } catch (err) {
      console.error("❌ Fetch error:", err);
      setError("Failed to load data for the map. Please try again.");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchStoresAndCampaigns();
  }, [fetchStoresAndCampaigns]);

  return (
    <div className="container">
      <div className="page-header">
        <h2 className="page-title">Dashboard Overview</h2>
      </div>

      <div className="card card-full">
        <div className="card-header flex-between">
          <h4 className="card-title">Store Locations</h4>
          <button className="btn btn-sm btn-secondary" onClick={fetchStoresAndCampaigns}>
            Refresh
          </button>
        </div>

        <div className="card-body">
          {loading && <LoadingMessage message="Loading map data..." />}
          <ErrorMessage message={error} />

          {!loading && !error && (
            <StoresMap
              stores={stores}
              districts={districts}
              districtMap={districtMap}
              campaigns={campaigns}
              campaignStores={campaignStores}
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default DashBoardPage;
