// src/pages/CampaignDetailPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';

function CampaignDetailPage() {
  const { id } = useParams();
  const [campaign, setCampaign] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCampaign = async () => {
      try {
        const response = await api.get(`/campaigns/${id}`);
        setCampaign(response.data.data);
      } catch (err) {
        setError('Failed to fetch campaign details.');
      } finally {
        setLoading(false);
      }
    };
    fetchCampaign();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  if (!campaign) return <div>No campaign found</div>;

  return (
    <div>
      <h2>{campaign.name}</h2>
      <p><b>Description:</b> {campaign.description}</p>
      <p><b>Start:</b> {new Date(campaign.start_date).toLocaleDateString()}</p>
      <p><b>End:</b> {new Date(campaign.end_date).toLocaleDateString()}</p>
      <p><b>Status:</b> {campaign.status}</p>
    </div>
  );
}

export default CampaignDetailPage;
