import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';

function UserProfilePage() {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchUser = useCallback(async () => {
    setLoading(true);
    try {
      const response = await api.get(`/users/${id}`);
      setUser(response.data.data);
    } catch {
      setError('Failed to fetch user profile.');
    } finally {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchUser();
  }, [fetchUser]);

  if (loading) return <div>Loading profile...</div>;
  if (error) return <div className="text-danger">{error}</div>;
  if (!user) return <div>User not found.</div>;

  return (
    <div className="container">
      <Link to="/users" className="mb-4 inline-block">&larr; Back to User List</Link>

      <div className="profile-page flex flex-col md:flex-row gap-4">
        {/* Sidebar */}
        <div className="profile-sidebar flex-shrink-0 w-full md:w-1/3 space-y-4">
          {/* Avatar + Basic Info */}
          <div className="card text-center">
            <div className="card-body">
              <div className="profile-avatar mb-2">
                <span>AVT</span>
              </div>
              <h3>{user.full_name}</h3>
              <p className="text-muted">{user.role}</p>
            </div>
          </div>

          {/* Account Details */}
          <div className="card">
            <div className="card-header">
              <h4>Account Details</h4>
            </div>
            <div className="card-body space-y-2">
              {[
                { label: 'Full Name', value: user.full_name },
                { label: 'Email', value: user.email },
                { label: 'Phone Number', value: user.phone_number || 'N/A' },
                { label: 'Status', value: (
                  <span className={`status ${user.is_active ? 'status-active' : 'status-inactive'}`}>
                    {user.is_active ? 'Active' : 'Inactive'}
                  </span>
                )},
                { label: 'Joined Date', value: new Date(user.created_at).toLocaleDateString('vi-VN') },
              ].map(item => (
                <div className="detail-row flex justify-between" key={item.label}>
                  <div className="detail-label font-semibold">{item.label}</div>
                  <div className="detail-value">{item.value}</div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="profile-main flex-1 space-y-4">
          <div className="card">
            <div className="card-header">
              <h4>Additional Info</h4>
            </div>
            <div className="card-body">
              <p>No additional information available.</p>
              {/* Có thể thêm bảng activity, logs, hoặc các chi tiết khác */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default UserProfilePage;
