import React, { useState, useEffect } from 'react';
import api from '../services/api';
import UserForm from '../components/UserForm'; // Import the new form

function UserListPage() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentUser, setCurrentUser] = useState(null);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await api.get('/users');
                setUsers(response.data.data);
            } catch (err) {
                setError('Failed to fetch users.');
            } finally {
                setLoading(false);
            }
        };
        fetchUsers();
    }, []);
    
    const handleOpenEditModal = (user) => {
        setCurrentUser(user);
        setIsModalOpen(true);
    };

    const handleOpenCreateModal = () => {
        setCurrentUser(null);
        setIsModalOpen(true);
    };
    
    const handleSaveUser = async (userData) => {
        try {
            if (userData.id) { // Chế độ Sửa
                const { id, ...updateData } = userData;
                const response = await api.put(`/users/${id}`, updateData);
                setUsers(users.map(u => u.id === id ? response.data.data : u));
            } else { // Chế độ Tạo mới
                const response = await api.post('/users', userData);
                setUsers([response.data.data, ...users]);
            }
            setIsModalOpen(false);
        } catch (err) {
            setError(userData.id ? 'Failed to update user.' : 'Failed to create user.');
        }
    };
    
    // Thay vì xóa cứng, chúng ta nên "hủy kích hoạt" (soft delete)
    const handleDeactivateUser = async (user) => {
        if (window.confirm(`Are you sure you want to deactivate user ${user.full_name}?`)) {
            try {
                const updatedUser = { ...user, is_active: false };
                const response = await api.put(`/users/${user.id}`, updatedUser);
                setUsers(users.map(u => u.id === user.id ? response.data.data : u));
            } catch (err) {
                setError('Failed to deactivate user.');
            }
        }
    };

    return (
        <div>
            <h2>User Management</h2>
            <button onClick={handleOpenCreateModal} style={{ marginBottom: '20px' }}>
                Add New User
            </button>
            
            {/* ... (phần table) ... */}
            <tbody>
                {users.map(user => (
                    <tr key={user.id}>
                        {/* ... (các cột khác) ... */}
                        <td>
                            <button onClick={() => handleOpenEditModal(user)}>Edit</button>
                            {/* Chỉ hiện nút Deactivate nếu user đang active */}
                            {user.is_active && (
                                <button onClick={() => handleDeactivateUser(user)} style={{ marginLeft: '5px', backgroundColor: '#f44336' }}>
                                    Deactivate
                                </button>
                            )}
                        </td>
                    </tr>
                ))}
            </tbody>
            {/* ... */}
        </div>
    );
}

export default UserListPage;
