import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';
import AssignStoreModal from '../components/modal/AssignStoreModal';
import LoadingMessage from '../components/LoadingMessage';
import ErrorMessage from '../components/ErrorMessage';
import EmptyState from '../components/EmptyState';

const formatDate = (dateStr) => (dateStr ? new Date(dateStr).toLocaleDateString('vi-VN') : 'N/A');

function CampaignDetailPage() {
  const { id } = useParams();
  const [campaign, setCampaign] = useState(null);
  const [assignedStores, setAssignedStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  const fetchData = useCallback(async () => {
    setLoading(true);
    try {
      const [campaignRes, storesRes] = await Promise.all([
        api.get(`/campaigns/${id}`),
        api.get(`/campaigns/${id}/stores`),
      ]);
      setCampaign(campaignRes.data.data);
      setAssignedStores(storesRes.data.data || []);
      setError('');
    } catch {
      setError('Không thể tải thông tin chiến dịch.');
    } finally {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const handleSaveStores = () => {
    setIsModalOpen(false);
    fetchData();
  };

  if (loading) return <LoadingMessage text="Đang tải chiến dịch..." />;
  if (error) return <ErrorMessage text={error} />;
  if (!campaign) return <EmptyState message="Chiến dịch không tồn tại." />;

  return (
    <div className="container detail-page">
      {/* Sidebar */}
      <aside className="detail-sidebar">
        <div className="card text-center">
          <div className="card-body">
            <div className="avatar mb-2">📢</div>
            <h3 className="mb-1">{campaign.name}</h3>
            <span className={campaign.is_active ? 'badge badge-success' : 'badge badge-error'}>
              {campaign.is_active ? 'Active' : 'Inactive'}
            </span>
          </div>
        </div>

        <div className="card">
          <div className="card-header">Chi tiết</div>
          <div className="card-body detail-list">
            <div className="detail-row">
              <span className="detail-label">Mô tả</span>
              <span className="detail-value">{campaign.description || 'N/A'}</span>
            </div>
            <div className="detail-row">
              <span className="detail-label">Ngày bắt đầu</span>
              <span className="detail-value">{formatDate(campaign.start_date)}</span>
            </div>
            <div className="detail-row">
              <span className="detail-label">Ngày kết thúc</span>
              <span className="detail-value">{formatDate(campaign.end_date)}</span>
            </div>
          </div>
        </div>
      </aside>

      {/* Main Content */}
      <main className="detail-main">
        <div className="card card-stores">
          <div className="card-header flex-between">
            <h4>Số cửa hàng ({assignedStores.length})</h4>
            <button className="btn btn-primary btn-sm" onClick={() => setIsModalOpen(true)}>
              Quản lý cửa hàng
            </button>
          </div>
          <div className="card-body">
            {assignedStores.length > 0 ? (
              <ul className="card-list">
                {assignedStores.map((store) => (
                  <li key={store.id} className="card-item">
                    <strong>{store.store_code}</strong> - {store.address}
                  </li>
                ))}
              </ul>
            ) : (
              <EmptyState message="Chưa có cửa hàng được phân công." />
            )}
          </div>
        </div>
      </main>

      {/* Assign Store Modal */}
      <AssignStoreModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={handleSaveStores}
        campaignId={id}
        initiallyAssignedIds={assignedStores.map((s) => s.id)}
      />
    </div>
  );
}

export default CampaignDetailPage;
