// File: StoreDetailModal.js
import React from 'react';
import Modal from './Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt } from '@fortawesome/free-solid-svg-icons';
import { provinces, districtMap } from '../constants/locationData';

function StoreDetailModal({ isOpen, onClose, store }) {
  if (!isOpen || !store) return null;

  const getProvinceLabel = (value) => provinces.find(p => p.value === value)?.label || value;
  const getDistrictLabel = (province, districtRaw) =>
    districtMap[province]?.find(d => d.value === districtRaw)?.label || districtRaw;

  const googleMapsUrl = store.latitude && store.longitude
    ? `https://www.google.com/maps?q=${store.latitude},${store.longitude}`
    : null;

  const googleMapsEmbedUrl = store.latitude && store.longitude && process.env.REACT_APP_GOOGLE_MAPS_API_KEY
    ? `https://www.google.com/maps/embed/v1/view?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&center=${store.latitude},${store.longitude}&zoom=16`
    : null;

  const renderRow = (label, value) => (
    <div className="store-detail-row">
      <div className="store-detail-label">{label}</div>
      <div className="store-detail-value">{value || 'N/A'}</div>
    </div>
  );

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div className="modal-content store-detail-modal">
        <h3 className="store-detail-title">Store Details</h3>

        {renderRow('Board Name', store.board_name)}
        {renderRow('Store Code', store.store_code)}
        {renderRow('Address', store.address || store.address_vi)}
        {renderRow('Province', getProvinceLabel(store.district))}
        {renderRow('District', getDistrictLabel(store.district, store.district_raw))}
        {renderRow('Status', (
          <span className={`status status-${store.is_active ? 'active' : 'inactive'}`}>
            {store.is_active ? 'Active' : 'Inactive'}
          </span>
        ))}

        <div className="store-detail-row">
          <div className="store-detail-label">Location</div>
          <div className="store-detail-value">
            {googleMapsEmbedUrl ? (
              <iframe
                className="map-iframe store-map-iframe"
                src={googleMapsEmbedUrl}
                allowFullScreen
                loading="lazy"
                referrerPolicy="no-referrer-when-downgrade"
                title="Google Maps Location"
              />
            ) : (
              <p className="empty-state-message">
                {store.latitude && store.longitude
                  ? 'Google Maps API Key not configured.'
                  : 'No location data available.'}
              </p>
            )}
            {googleMapsUrl && (
              <a href={googleMapsUrl} target="_blank" rel="noopener noreferrer">
                <button className="btn btn-outline store-map-button">
                  <FontAwesomeIcon icon={faMapMarkerAlt} />
                  View on Google Maps
                </button>
              </a>
            )}
          </div>
        </div>

        <div className="modal-actions">
          <button className="btn btn-danger" onClick={onClose}>Close</button>
        </div>
      </div>
    </Modal>
  );
}

export default StoreDetailModal;
